// AuthContext.js
import React, { createContext, useContext, useState } from 'react';

// Criando o contexto de autenticação
const AuthContext = createContext();

// Hook para acessar o contexto de autenticação
export function useAuth() {
  return useContext(AuthContext);
}

// Componente provedor do contexto de autenticação
export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);

  const login = async (email, password) => {
    try {
      // Simulando lógica de login
      const fakeResponse = { data: { email, name: 'Usuário Exemplo' } };
      setUser(fakeResponse.data);
      return { success: true };
    } catch (error) {
      console.error('Erro ao fazer login:', error);
      return { success: false };
    }
  };

  const logout = () => {
    setUser(null);
  };

  // Valor do contexto que será disponibilizado para os componentes descendentes
  const contextValue = {
    user,
    login,
    logout,
  };

  return (
    <AuthContext.Provider value={contextValue}>
      {children}
    </AuthContext.Provider>
  );
}
