import React, { useState } from 'react';
import { FaSuitcase, FaHouse, FaLocationPin, FaRss, FaArrowLeft } from 'react-icons/fa6';
import { Link } from 'react-router-dom';

export function EditProfile() {
  // Estados para armazenar os dados do perfil editável
  const [businessType, setBusinessType] = useState('Importação e exportação de produtos diversos');
  const [address, setAddress] = useState('Edifício Azul AP. 101 - 3º Andar');
  const [location, setLocation] = useState('Luanda, Angola - Largo da Mutamba');
  const [connections, setConnections] = useState(97);

  // Função para salvar as alterações no perfil
  const saveChanges = () => {
    // Lógica para salvar as alterações no perfil
    console.log('Alterações salvas!');
    // Aqui você pode adicionar a lógica para enviar os dados atualizados para o servidor, se necessário
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen py-6 bg-gray-100">
      <div className="max-w-xl w-full bg-white shadow-md rounded-lg overflow-hidden">
        {/* Cabeçalho da página */}
        <div className="bg-gray-900 px-4 py-6">
          <div className="flex items-center justify-between">
            <h1 className="text-2xl font-bold text-white">Editar Perfil</h1>
            <Link to="/" className="text-white hover:underline">
              <FaArrowLeft className="inline-block mr-1" /> Voltar
            </Link>
          </div>
        </div>

        {/* Formulário de edição do perfil */}
        <div className="p-6">
          <div className="space-y-4">
            {/* Campo de Tipo de Negócio */}
            <div className="flex items-center">
              <FaSuitcase className="text-gray-600 mr-2" />
              <input
                type="text"
                value={businessType}
                onChange={(e) => setBusinessType(e.target.value)}
                className="w-full rounded-md border-gray-300 focus:border-blue-500 focus:ring-blue-500"
              />
            </div>

            {/* Campo de Endereço */}
            <div className="flex items-center">
              <FaHouse className="text-gray-600 mr-2" />
              <input
                type="text"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                className="w-full rounded-md border-gray-300 focus:border-blue-500 focus:ring-blue-500"
              />
            </div>

            {/* Campo de Localização */}
            <div className="flex items-center">
              <FaLocationPin className="text-gray-600 mr-2" />
              <input
                type="text"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                className="w-full rounded-md border-gray-300 focus:border-blue-500 focus:ring-blue-500"
              />
            </div>

            {/* Campo de Número de Conexões */}
            <div className="flex items-center">
              <FaRss className="text-gray-600 mr-2" />
              <input
                type="number"
                value={connections}
                onChange={(e) => setConnections(e.target.value)}
                className="w-full rounded-md border-gray-300 focus:border-blue-500 focus:ring-blue-500"
              />
            </div>
          </div>

          {/* Botões de ação */}
          <div className="mt-6 flex justify-end space-x-4">
            <button
              onClick={saveChanges}
              className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 focus:outline-none"
            >
              <FaSave className="inline-block mr-1" /> Salvar Alterações
            </button>
            <Link to="/" className="px-4 py-2 bg-gray-300 text-gray-800 rounded hover:bg-gray-400 focus:outline-none">
              Cancelar
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
}
