import React, { useState } from 'react';
import { PrincipalPage } from '../../layout/principalPage';
import "./userpage.css";
import { CreatePost } from '../../components/CreatePost/CreatePost';
import { MainPost } from '../../components/MainPost/MainPost';

export function UserPage() {
  // Estado para armazenar os posts
  const [posts, setPosts] = useState([]);

  // Função para criar um novo post
  const handleCreatePost = ({ text, image, type }) => {
    const newPost = {
      id: posts.length + 1,
      text,
      image,
      type,
      likes: 0,
      comments: [],
      isLiked: false,
    };
    setPosts([newPost, ...posts]);
  };

  // Função para lidar com a ação de curtir um post
  const handleLikePost = (postId) => {
    const updatedPosts = posts.map(post => {
      if (post.id === postId) {
        post.isLiked = !post.isLiked;
        post.likes += post.isLiked ? 1 : -1;
      }
      return post;
    });
    setPosts(updatedPosts);
  };

  // Função para lidar com a adição de um comentário a um post
  const handleCommentPost = (postId, commentText) => {
    const updatedPosts = posts.map(post => {
      if (post.id === postId) {
        post.comments.push({ author: "Current User", text: commentText });
      }
      return post;
    });
    setPosts(updatedPosts);
  };

  // Função para lidar com a exclusão de um post
  const handleDeletePost = (postId) => {
    const updatedPosts = posts.filter(post => post.id !== postId);
    setPosts(updatedPosts);
  };

  return (
    <PrincipalPage>
      <div className="home-page-cards flex flex-col lg:flex-row justify-between rounded-md">
        {/* Coluna da esquerda */}
        <div className="home-page-col-one bg-gray-800 rounded-md hidden lg:block w-full lg:w-1/5 h-96">
          {/* Conteúdo da coluna da esquerda */}
        </div>

        {/* Coluna do meio */}
        <div className="home-page-col-two w-full lg:w-3/5 bg-gray-900 flex flex-col p-4">
          {/* Componente de criação de post */}
          <CreatePost onCreatePost={handleCreatePost} />
          {/* Mapeamento dos posts e renderização dos componentes MainPost */}
          {posts.map(post => (
            <MainPost 
              key={post.id} 
              post={post} 
              onLike={handleLikePost} 
              onComment={handleCommentPost} 
              onDelete={handleDeletePost}
            />
          ))}
        </div>

        {/* Coluna da direita */}
        <div className="home-page-col-three bg-gray-800 rounded-md hidden lg:block w-full lg:w-1/5 h-96 p-4">
          {/* Conteúdo da coluna da direita */}
        </div>
      </div>
    </PrincipalPage>
  );
}
