import React, { useState } from 'react';
import axios from 'axios';
import { Link, Navigate } from 'react-router-dom';
import sucessgif from '../../assets/img/icons8-sucesso.gif';
import errogif from '../../assets/img/icons8-erro.gif';
import provinciasData from '../../JSON/provincias.json';
import areasAtuacaoData from '../../JSON/areasAtuacao.json';

export function Register() {
  const [gestorName, setGestorName] = useState('');
  const [password, setPassword] = useState('');
  const [nameCompany, setNameCompany] = useState('');
  const [email, setEmail] = useState('');
  const [province, setProvince] = useState('');
  const [streetAddress, setStreetAddress] = useState('');
  const [nifEmpresa, setNifEmpresa] = useState('');
  const [actuacao, setActuacao] = useState('');
  const [telefone, setTelefone] = useState('');
  const [nifPessoal, setNifPessoal] = useState('');
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [showErrorModal, setShowErrorModal] = useState(false);
  const [userType, setUserType] = useState('');

  const handleCloseSuccessModal = () => setShowSuccessModal(false);
  const handleCloseErrorModal = () => setShowErrorModal(false);

  const handleRegister = (e) => {
    e.preventDefault();

    const user = {
      gestorName,
      password,
      nameCompany,
      email,
      province,
      streetAddress,
      nifEmpresa: userType === 'empresa' ? nifEmpresa : '',
      nifPessoal: userType === 'empreendedor' ? nifPessoal : '',
      actuacao: userType === 'empreendedor' ? actuacao : '',
      telefone,
    };

    axios
      .post('http://localhost:3001/register', user)
      .then((response) => {
        setShowSuccessModal(true);
        setTimeout(() => {
          setShowSuccessModal(false);
          Navigate('/login');
        }, 3000);
      })
      .catch((error) => {
        setShowErrorModal(true);
        setTimeout(() => {
          setShowErrorModal(false);
        }, 2000);
      });
  };

  return (
    <>
      <section className="bg-gray-900 min-h-screen flex items-center justify-center">
        <div className="max-w-3xl mx-auto px-4 py-12 lg:px-8">
          <div className="bg-white p-8 rounded-lg shadow-lg">
            <div className="mb-8 text-center">
              <h1 className="text-xl font-semibold text-gray-800">Selecione o Tipo de Usuário</h1>
              <div className="mt-4 space-x-4">
                <button
                  onClick={() => setUserType('empreendedor')}
                  className={`px-6 py-3 rounded-md text-white focus:outline-none ${
                    userType === 'empreendedor' ? 'bg-blue-500 hover:bg-blue-600' : 'bg-gray-600 hover:bg-gray-700'
                  }`}
                >
                  Empreendedor
                </button>
                <button
                  onClick={() => setUserType('empresa')}
                  className={`px-6 py-3 rounded-md text-white focus:outline-none ${
                    userType === 'empresa' ? 'bg-blue-500 hover:bg-blue-600' : 'bg-gray-600 hover:bg-gray-700'
                  }`}
                >
                  Empresa
                </button>
              </div>
            </div>

            {userType && (
              <form onSubmit={handleRegister}>
                {/* Formulário de Registro */}
                <div className="space-y-6">
                  {/* Campos de acordo com o tipo de usuário selecionado */}
                  {userType === 'empresa' && (
                    <>
                      <div>
                        <label htmlFor="nameCompany" className="block text-sm font-medium text-gray-700">
                          Nome da Empresa
                        </label>
                        <input
                          type="text"
                          id="nameCompany"
                          name="nameCompany"
                          value={nameCompany}
                          onChange={(e) => setNameCompany(e.target.value)}
                          required
                          autoComplete="organization"
                          className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                        />
                      </div>
                      <div>
                        <label htmlFor="nifEmpresa" className="block text-sm font-medium text-gray-700">
                          NIF da Empresa
                        </label>
                        <input
                          type="text"
                          id="nifEmpresa"
                          name="nifEmpresa"
                          value={nifEmpresa}
                          onChange={(e) => setNifEmpresa(e.target.value)}
                          minLength={9}
                          maxLength={9}
                          required
                          autoComplete="off"
                          className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                        />
                      </div>
                    </>
                  )}

                  {userType === 'empreendedor' && (
                    <>
                      <div>
                        <label htmlFor="gestorName" className="block text-sm font-medium text-gray-700">
                          Nome do Representante
                        </label>
                        <input
                          type="text"
                          id="gestorName"
                          name="gestorName"
                          value={gestorName}
                          onChange={(e) => setGestorName(e.target.value)}
                          required
                          autoComplete="given-name"
                          className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                        />
                      </div>
                      <div>
                        <label htmlFor="actuacao" className="block text-sm font-medium text-gray-700">
                          Área de Atuação
                        </label>
                        <select
                          id="actuacao"
                          name="actuacao"
                          value={actuacao}
                          onChange={(e) => setActuacao(e.target.value)}
                          required
                          autoComplete="off"
                          className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                        >
                          <option value="">Selecione a Área de Atuação</option>
                          {areasAtuacaoData.areas.map((area, index) => (
                            <option key={index} value={area}>
                              {area}
                            </option>
                          ))}
                        </select>
                      </div>
                      <div>
                        <label htmlFor="nifPessoal" className="block text-sm font-medium text-gray-700">
                          NIF Pessoal
                        </label>
                        <input
                          type="text"
                          id="nifPessoal"
                          name="nifPessoal"
                          value={nifPessoal}
                          onChange={(e) => setNifPessoal(e.target.value)}
                          minLength={9}
                          maxLength={9}
                          required
                          autoComplete="off"
                          className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                        />
                      </div>
                    </>
                  )}

                  {/* Campos comuns para ambos os tipos de usuário */}
                  <div>
                    <label htmlFor="password" className="block text-sm font-medium text-gray-700">
                      Palavra-passe
                    </label>
                    <input
                      type="password"
                      id="password"
                      name="password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                      minLength={8}
                      autoComplete="new-password"
                      className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    />
                  </div>
                  <div>
                    <label htmlFor="email" className="block text-sm font-medium text-gray-700">
                      Endereço Email
                    </label>
                    <input
                      id="email"
                      name="email"
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      autoComplete="email"
                      required
                      className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    />
                  </div>
                  <div>
                    <label htmlFor="province" className="block text-sm font-medium text-gray-700">
                      Província
                    </label>
                    <select
                      id="province"
                      name="province"
                      value={province}
                      onChange={(e) => setProvince(e.target.value)}
                      required
                      autoComplete="address-level1"
                      className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    >
                      <option value="">Selecione a Província</option>
                      {provinciasData.provincias.map((prov, index) => (
                        <option key={index} value={prov}>
                          {prov}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label htmlFor="streetAddress" className="block text-sm font-medium text-gray-700">
                      Endereço da Empresa
                    </label>
                    <input
                      type="text"
                      id="streetAddress"
                      name="streetAddress"
                      value={streetAddress}
                      onChange={(e) => setStreetAddress(e.target.value)}
                      required
                      autoComplete="street-address"
                      className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    />
                  </div>
                  <div>
                    <label htmlFor="telefone" className="block text-sm font-medium text-gray-700">
                      Telefone
                    </label>
                    <input
                      type="text"
                      id="telefone"
                      name="telefone"
                      value={telefone}
                      onChange={(e) => setTelefone(e.target.value)}
                      minLength={9}
                      maxLength={9}
                      required
                      autoComplete="off"
                      className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                    />
                  </div>
                </div>

                {/* Botões de Ação */}
                <div className="mt-6 flex justify-end">
                  <Link to="/" className="inline-block px-4 py-2 rounded-md text-white bg-gray-600 hover:bg-gray-700">
                    Cancelar
                  </Link>
                  <button
                    type="submit"
                    className="ml-4 inline-block px-4 py-2 rounded-md text-white bg-blue-500 hover:bg-blue-600"
                  >
                    Registrar
                  </button>
                </div>
              </form>
            )}
          </div>
        </div>
      </section>

      {/* Modais */}
      {showSuccessModal && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white p-8 max-w-sm rounded-lg shadow-lg flex flex-col items-center">
            <img src={sucessgif} alt="Ícone de Sucesso" className="w-24 h-24 mb-4" />
            <p className="text-lg font-semibold text-gray-800 mb-2">Registro realizado com sucesso!</p>
            <p className="text-sm text-gray-600">Você será redirecionado para a página de login.</p>
          </div>
        </div>
      )}

      {showErrorModal && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="bg-white p-8 max-w-sm rounded-lg shadow-lg flex flex-col items-center">
            <img src={errogif} alt="Ícone de Erro" className="w-24 h-24 mb-4" />
            <p className="text-lg font-semibold text-gray-800 mb-2">Ops! Algo deu errado.</p>
            <p className="text-sm text-gray-600">Tente novamente mais tarde.</p>
          </div>
        </div>
      )}
    </>
  );
}

export default Register;
