import React, { useState } from "react";
import CarexLogo from "../../assets/img/carex_logo.png";
import { FaThumbsUp, FaShare, FaChevronCircleRight } from 'react-icons/fa';

export function MainPost({ post, onLike, onComment, onDelete }) {
  const [commentText, setCommentText] = useState("");
  const [showMenu, setShowMenu] = useState(false);

  // Função para adicionar um novo comentário
  const handleAddComment = () => {
    if (commentText.trim() !== "") {
      onComment(post.id, commentText);
      setCommentText("");
    }
  };

  // Obter o autor do último comentário
  const lastComment = post.comments.length > 0 ? post.comments[post.comments.length - 1] : null;
  const lastCommentAuthor = lastComment ? lastComment.author : null;

  return (
    <div style={{ backgroundColor: "#1a202c", border: "1px solid #2d3748", borderRadius: "0.375rem", padding: "1rem", marginBottom: "1rem" }}>
      {/* Seção do autor e menu suspenso */}
      {lastCommentAuthor && (
        <div style={{ display: "flex", justifyContent: "space-between", color: "#a0aec0", marginBottom: "0.5rem" }}>
          <span style={{ cursor: "pointer" }}>
            <span style={{ color: "white", fontWeight: "bold" }}>{lastCommentAuthor}</span> comentou isto
          </span>
          <div style={{ position: "relative" }}>
            <button onClick={() => setShowMenu(!showMenu)} style={{ color: "white", cursor: "pointer" }}>...</button>
            {showMenu && (
              <div style={{ position: "absolute", top: "100%", right: "0", backgroundColor: "#2d3748", borderRadius: "0.375rem", zIndex: "1000" }}>
                <button onClick={() => onDelete(post.id)} style={{ padding: "0.5rem 1rem", color: "#a0aec0", background: "none", border: "none", cursor: "pointer" }}>Eliminar</button>
                <button onClick={() => alert('Outra opção')} style={{ padding: "0.5rem 1rem", color: "#a0aec0", background: "none", border: "none", cursor: "pointer" }}>Editar</button>
              </div>
            )}
          </div>
        </div>
      )}
      <div style={{ width: "100%", height: "1px", backgroundColor: "#4a5568", margin: "0.25rem 0" }}></div>
      {/* Detalhes do autor do post */}
      <div style={{ display: "flex", alignItems: "center", marginBottom: "0.25rem" }}>
        <img src={CarexLogo} alt="Carex Logo" style={{ width: "3rem", height: "3rem", borderRadius: "9999px" }} />
        <div style={{ marginLeft: "1rem" }}>
          <span style={{ color: "white", fontWeight: "bold", cursor: "pointer" }}>Carex Angola</span>
          <span style={{ color: "#a0aec0", fontSize: "0.875rem" }}> Despachante e Transportadora</span>
        </div>
      </div>
      {/* Conteúdo principal do post (texto e imagem) */}
      <div style={{ color: "white", marginBottom: "0.5rem", textAlign: "justify" }}>
        {post.type && <span style={{ display: "block", marginBottom: "0.5rem", color: "#4299e1", fontWeight: "bold" }}>{post.type}</span>}
        <p style={{ marginBottom: "1rem" }}>{post.text}</p>
        {post.image && <img src={post.image} alt="Post" style={{ width: "100%", marginBottom: "1rem" }} />}
      </div>
      {/* Botões de curtir e compartilhar */}
      <div style={{ display: "flex", justifyContent: "space-evenly", alignItems: "center", marginBottom: "1rem" }}>
        <button onClick={() => onLike(post.id)} style={{ display: "flex", alignItems: "center", color: post.isLiked ? "#f56565" : "#4299e1" }}>
          <FaThumbsUp style={{ marginRight: "0.5rem" }} /> {post.isLiked ? 'Descurtir' : 'Curtir'} {post.likes}
        </button>
        <button style={{ display: "flex", alignItems: "center", color: "#4299e1" }}><FaShare style={{ marginRight: "0.5rem" }} /> Compartilhar</button>
      </div>
      {/* Seção de adicionar comentário */}
      <div style={{ backgroundColor: "#2d3748", borderRadius: "0.375rem", padding: "0.5rem", marginBottom: "0.5rem", display: "flex", justifyContent: "space-between" }}>
        <input
          type="text"
          placeholder='Comentar...'
          style={{ width: "100%", height: "2.5rem", borderRadius: "9999px", paddingLeft: "1rem", border: "1px solid #4a5568", backgroundColor: "#4a5568", color: "white" }}
          value={commentText}
          onChange={(e) => setCommentText(e.target.value)}
        />
        <button onClick={handleAddComment} style={{ color: "#4299e1", fontSize: "1.875rem", marginLeft: "0.5rem" }}>
          <FaChevronCircleRight />
        </button>
      </div>
      {/* Comentários do post */}
      {post.comments.slice().reverse().map((comment, index) => (
        <div key={index} style={{ backgroundColor: "#2d3748", borderRadius: "0.375rem", padding: "0.5rem", marginBottom: "0.5rem" }}>
          <div style={{ display: "flex", justifyContent: "space-between", color: "#a0aec0", marginBottom: "0.5rem", fontSize: "0.875rem" }}>
            <span style={{ color: "white", fontWeight: "bold" }}>{comment.author}</span>
            <button style={{ color: "#4299e1" }}><FaThumbsUp style={{ marginRight: "0.5rem" }} /></button>
          </div>
          <p style={{ color: "white", fontSize: "0.875rem" }}>{comment.text}</p>
        </div>
      ))}
    </div>
  );
}
