import { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Logo } from '../../components/logo';

export function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [showErrorModal, setShowErrorModal] = useState(false);
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    axios.post('http://localhost:3001/login', { email, password })
      .then(response => {
        setShowSuccessModal(true);
        setTimeout(() => {
          setShowSuccessModal(false);
          navigate('/userpage');
        }, 3000); // Fecha o modal após 3 segundos e redireciona para a página do usuário
      })
      .catch(error => {
        setShowErrorModal(true);
        setTimeout(() => {
          setShowErrorModal(false);
        }, 2000); // Fecha o modal de erro após 2 segundos
      });
  };

  return (
    <>
      <section className="min-h-screen flex items-center justify-center bg-gray-900">
        <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-lg">
          <Logo className="mx-auto mb-4" />
          <h2 className="text-center text-2xl font-bold text-gray-900 mb-6">Entre na sua conta</h2>
          <form onSubmit={handleLogin} className="space-y-4">
            <div>
              <label htmlFor="email" className="block text-sm font-medium leading-6 text-gray-900">Endereço Email</label>
              <input id="email" name="email" type="email" value={email} onChange={(e) => setEmail(e.target.value)} autoComplete="email" required className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-600 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"/>
            </div>
            <div>
              <label htmlFor="password" className="block text-sm font-medium leading-6 text-gray-900">Palavra-passe</label>
              <input id="password" name="password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} autoComplete="current-password" required className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-600 focus:ring focus:ring-indigo-200 focus:ring-opacity-50"/>
            </div>
            <div>
              <button type="submit" className="w-full bg-indigo-600 hover:bg-indigo-700 text-white py-2 px-4 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-opacity-50">Entrar</button>
            </div>
          </form>
          <p className="mt-4 text-center text-sm">
            Não tens uma conta?{' '}
            <Link to="/register" className="font-medium text-indigo-600 hover:text-indigo-500">Faça o seu Registro</Link>
          </p>
        </div>
      </section>

      {/* Modal de Sucesso */}
      {showSuccessModal &&
        <div className="fixed inset-0 z-50 flex items-center justify-center overflow-x-hidden overflow-y-auto bg-gray-900 bg-opacity-90">
          <div className="bg-white p-6 rounded-lg shadow-lg flex flex-col items-center">
            <img src="/path/to/success.svg" alt="Success" className="h-20 mb-4" />
            <p className="text-lg text-gray-900">Login bem-sucedido!</p>
          </div>
        </div>
      }

      {/* Modal de Erro */}
      {showErrorModal &&
        <div className="fixed inset-0 z-50 flex items-center justify-center overflow-x-hidden overflow-y-auto bg-gray-900 bg-opacity-90">
          <div className="bg-white p-6 rounded-lg shadow-lg flex flex-col items-center">
            <img src="/path/to/error.svg" alt="Error" className="h-20 mb-4" />
            <p className="text-lg text-gray-900">Credenciais inválidas.</p>
          </div>
        </div>
      }
    </>
  );
}
