import React, { useState, useEffect } from 'react';
import { Dialog } from '@headlessui/react';
import { Header } from '../../components/Header';

export function Register() {
    const [tipo, setTipo] = useState('');
    const [formData, setFormData] = useState({
        email: '',
        endereco: '',
        telefone: '',
        area_atuacao: '',
        provincia: '',
        senha: '',
        nome_representante: '',
        nome_empresa: '',
        nif_empresa: '',
        nome: '',
        genero: '',
        data_nascimento: '',
        bi: ''
    });
    const [areas, setAreas] = useState([]);
    const [provincias, setProvincias] = useState([]);
    const [isSuccessModalOpen, setIsSuccessModalOpen] = useState(false);
    const [isErrorModalOpen, setIsErrorModalOpen] = useState(false);

    useEffect(() => {
        fetch('/areas.json')
            .then(response => response.json())
            .then(data => setAreas(data))
            .catch(error => console.error('Erro ao carregar áreas:', error));

        fetch('/provincias.json')
            .then(response => response.json())
            .then(data => setProvincias(data))
            .catch(error => console.error('Erro ao carregar províncias:', error));
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Suponha que estamos enviando dados para um endpoint
        fetch('/api/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(formData)
        })
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    setIsSuccessModalOpen(true);
                } else {
                    setIsErrorModalOpen(true);
                }
            })
            .catch(error => {
                console.error('Erro ao registrar:', error);
                setIsErrorModalOpen(true);
            });
    };

    return (
        <div className="min-h-screen bg-gray-900 flex items-center justify-center">
            <Header />
            <form onSubmit={handleSubmit} className="bg-gray-800 p-8 rounded-lg shadow-lg w-full max-w-lg">
                <h2 className="text-2xl font-bold text-white mb-6">Registro</h2>
                <div className="mb-4">
                    <label className="block text-gray-400 mb-2">Tipo de Usuário</label>
                    <select
                        name="tipo"
                        value={tipo}
                        onChange={(e) => setTipo(e.target.value)}
                        className="w-full p-2 rounded bg-gray-700 text-white"
                    >
                        <option value="">Selecione</option>
                        <option value="empresa">Empresa</option>
                        <option value="empreendedor">Empreendedor</option>
                    </select>
                </div>
                <div className="mb-4">
                    <label className="block text-gray-400 mb-2">Email</label>
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        className="w-full p-2 rounded bg-gray-700 text-white"
                        required
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-400 mb-2">Endereço</label>
                    <input
                        type="text"
                        name="endereco"
                        value={formData.endereco}
                        onChange={handleInputChange}
                        className="w-full p-2 rounded bg-gray-700 text-white"
                        required
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-400 mb-2">Telefone</label>
                    <input
                        type="text"
                        name="telefone"
                        value={formData.telefone}
                        onChange={handleInputChange}
                        className="w-full p-2 rounded bg-gray-700 text-white"
                        required
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-400 mb-2">Área de Atuação</label>
                    <select
                        name="area_atuacao"
                        value={formData.area_atuacao}
                        onChange={handleInputChange}
                        className="w-full p-2 rounded bg-gray-700 text-white"
                        required
                    >
                        <option value="">Selecione</option>
                        {areas.map((area, index) => (
                            <option key={index} value={area}>{area}</option>
                        ))}
                    </select>
                </div>
                <div className="mb-4">
                    <label className="block text-gray-400 mb-2">Província</label>
                    <select
                        name="provincia"
                        value={formData.provincia}
                        onChange={handleInputChange}
                        className="w-full p-2 rounded bg-gray-700 text-white"
                        required
                    >
                        <option value="">Selecione</option>
                        {provincias.map((provincia, index) => (
                            <option key={index} value={provincia}>{provincia}</option>
                        ))}
                    </select>
                </div>
                <div className="mb-4">
                    <label className="block text-gray-400 mb-2">Senha</label>
                    <input
                        type="password"
                        name="senha"
                        value={formData.senha}
                        onChange={handleInputChange}
                        className="w-full p-2 rounded bg-gray-700 text-white"
                        required
                    />
                </div>
                {tipo === 'empresa' && (
                    <>
                        <div className="mb-4">
                            <label className="block text-gray-400 mb-2">Nome do Representante</label>
                            <input
                                type="text"
                                name="nome_representante"
                                value={formData.nome_representante}
                                onChange={handleInputChange}
                                className="w-full p-2 rounded bg-gray-700 text-white"
                                required
                            />
                        </div>
                        <div className="mb-4">
                            <label className="block text-gray-400 mb-2">Nome da Empresa</label>
                            <input
                                type="text"
                                name="nome_empresa"
                                value={formData.nome_empresa}
                                onChange={handleInputChange}
                                className="w-full p-2 rounded bg-gray-700 text-white"
                                required
                            />
                        </div>
                        <div className="mb-4">
                            <label className="block text-gray-400 mb-2">NIF da Empresa</label>
                            <input
                                type="text"
                                name="nif_empresa"
                                value={formData.nif_empresa}
                                onChange={handleInputChange}
                                className="w-full p-2 rounded bg-gray-700 text-white"
                                required
                            />
                        </div>
                    </>
                )}
                {tipo === 'empreendedor' && (
                    <>
                        <div className="mb-4">
                            <label className="block text-gray-400 mb-2">Nome</label>
                            <input
                                type="text"
                                name="nome"
                                value={formData.nome}
                                onChange={handleInputChange}
                                className="w-full p-2 rounded bg-gray-700 text-white"
                                required
                            />
                        </div>
                        <div className="mb-4">
                            <label className="block text-gray-400 mb-2">Gênero</label>
                            <select
                                name="genero"
                                value={formData.genero}
                                onChange={handleInputChange}
                                className="w-full p-2 rounded bg-gray-700 text-white"
                                required
                            >
                                <option value="">Selecione</option>
                                <option value="masculino">Masculino</option>
                                <option value="feminino">Feminino</option>
                            </select>
                        </div>
                        <div className="mb-4">
                            <label className="block text-gray-400 mb-2">Data de Nascimento</label>
                            <input
                                type="date"
                                name="data_nascimento"
                                value={formData.data_nascimento}
                                onChange={handleInputChange}
                                className="w-full p-2 rounded bg-gray-700 text-white"
                                required
                            />
                        </div>
                        <div className="mb-4">
                            <label className="block text-gray-400 mb-2">BI/Número de Identificação</label>
                            <input
                                type="text"
                                name="bi"
                                value={formData.bi}
                                onChange={handleInputChange}
                                className="w-full p-2 rounded bg-gray-700 text-white"
                                required
                            />
                        </div>
                    </>
                )}
                <button type="submit" className="w-full bg-indigo-600 text-white font-bold py-2 rounded hover:bg-indigo-500">Registrar</button>
            </form>

            {/* Modal de Sucesso */}
            <Dialog open={isSuccessModalOpen} onClose={() => setIsSuccessModalOpen(false)} className="fixed z-10 inset-0 overflow-y-auto">
                <div className="flex items-center justify-center min-h-screen bg-gray-900 bg-opacity-75 p-4 text-center">
                    <Dialog.Panel className="bg-gray-800 p-6 rounded-lg shadow-lg">
                        <Dialog.Title className="text-lg font-bold text-white">Registro bem-sucedido!</Dialog.Title>
                        <Dialog.Description className="mt-2 text-gray-400">Seu registro foi realizado com sucesso.</Dialog.Description>
                        <button onClick={() => setIsSuccessModalOpen(false)} className="mt-4 bg-indigo-600 text-white font-bold py-2 px-4 rounded hover:bg-indigo-500">Fechar</button>
                    </Dialog.Panel>
                </div>
            </Dialog>

            {/* Modal de Erro */}
            <Dialog open={isErrorModalOpen} onClose={() => setIsErrorModalOpen(false)} className="fixed z-10 inset-0 overflow-y-auto">
                <div className="flex items-center justify-center min-h-screen bg-gray-900 bg-opacity-75 p-4 text-center">
                    <Dialog.Panel className="bg-gray-800 p-6 rounded-lg shadow-lg">
                        <Dialog.Title className="text-lg font-bold text-white">Erro no Registro</Dialog.Title>
                        <Dialog.Description className="mt-2 text-gray-400">Houve um problema ao tentar realizar o registro. Tente novamente.</Dialog.Description>
                        <button onClick={() => setIsErrorModalOpen(false)} className="mt-4 bg-indigo-600 text-white font-bold py-2 px-4 rounded hover:bg-indigo-500">Fechar</button>
                    </Dialog.Panel>
                </div>
            </Dialog>
        </div>
    );
}
