import React, { createContext, useContext, useState } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null); // Estado para armazenar os dados do usuário logado

  // Função para definir o usuário (geralmente utilizada durante o login)
  const login = (userData) => {
    setUser(userData); // Define os dados do usuário no estado
  };

  // Função para deslogar o usuário (limpar os dados do estado)
  const logout = () => {
    setUser(null); // Limpa os dados do usuário no estado
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

// Hook personalizado para acessar o contexto de autenticação
export const useAuth = () => useContext(AuthContext);
