import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './ProfileEdit.module.css'; // Estilos específicos para o componente

import provinciasData from '../../JSON/provincias.json';
import areasAtuacaoData from '../../JSON/areasAtuacao.json';

export function ProfileEdit() {
  const [userType, setUserType] = useState(''); 
  const [gestorName, setGestorName] = useState('');
  const [password, setPassword] = useState('');
  const [nameCompany, setNameCompany] = useState('');
  const [email, setEmail] = useState('');
  const [province, setProvince] = useState('');
  const [streetAddress, setStreetAddress] = useState('');
  const [nifEmpresa, setNifEmpresa] = useState('');
  const [actuacao, setActuacao] = useState('');
  const [telefone, setTelefone] = useState('');
  const [profilePhoto, setProfilePhoto] = useState(null);
  const [coverPhoto, setCoverPhoto] = useState(null);
  const [showSuccessModal, setShowSuccessModal] = useState(false); 
  const [showErrorModal, setShowErrorModal] = useState(false); 

  const handleCloseSuccessModal = () => setShowSuccessModal(false);
  const handleCloseErrorModal = () => setShowErrorModal(false);

  useEffect(() => {
    // Exemplo de uso: preencher um select com as províncias de Angola
    console.log(provinciasData.provincias);
  }, []);

  const handleProfileUpdate = (e) => {
    e.preventDefault(); 

    const user = {
      gestorName,
      password,
      nameCompany,
      email,
      province,
      streetAddress,
      nifEmpresa,
      actuacao,
      telefone,
      profilePhoto,
      coverPhoto
    };

    axios.put('http://localhost:3001/profile', user)
      .then(response => {
        setShowSuccessModal(true); 
        setTimeout(() => {
          setShowSuccessModal(false);
        }, 3000);
      })
      .catch(error => {
        setShowErrorModal(true);
        setTimeout(() => {
          setShowErrorModal(false); 
        }, 2000);
      });
  };

  const handleProfilePhotoChange = (e) => {
    setProfilePhoto(e.target.files[0]);
  };

  const handleCoverPhotoChange = (e) => {
    setCoverPhoto(e.target.files[0]);
  };

  return (
    <>
      <section className={styles.containerProfileEdit}>
        <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8 bg-gray-900 opacity-90">
          <div className="max-w-3xl mx-auto px-4">
            <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8">
              <div className="mt-10 sm:mx-auto sm:w-full">
                <form onSubmit={handleProfileUpdate}>
                  <div className="space-y-12">
                    <div className="border-b border-gray-900/10 pb-12">
                      <h1 className="text-base font-semibold leading-7 text-white">Edite seu Perfil</h1>
                      <p className="mt-1 text-sm leading-6 text-white">Atualize suas informações e adicione uma foto de perfil e capa.</p>
                      <div className="mt-10 grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6">
                        <div className="sm:col-span-3">
                          <label htmlFor="gestorName" className="block text-sm font-medium leading-6 text-white">Nome do Representante</label>
                          <div className="mt-2">
                            <input type="text" name="gestorName" id="gestorName" value={gestorName} onChange={(e) => setGestorName(e.target.value)} required autoComplete="given-name" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="password" className="block text-sm font-medium leading-6 text-white">Palavra-passe</label>
                          <div className="mt-2">
                            <input type="password" name="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required minLength={8} autoComplete="new-password" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>

                        {userType === 'empresa' && (
                          <>
                            <div className="sm:col-span-3">
                              <label htmlFor="nameCompany" className="block text-sm font-medium leading-6 text-white">Nome da Empresa</label>
                              <div className="mt-2">
                                <input type="text" name="nameCompany" id="nameCompany" value={nameCompany} onChange={(e) => setNameCompany(e.target.value)} required autoComplete="organization" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                              </div>
                            </div>
                            <div className="sm:col-span-3">
                              <label htmlFor="streetAddress" className="block text-sm font-medium leading-6 text-white">Endereço da Empresa</label>
                              <div className="mt-2">
                                <input type="text" name="streetAddress" id="streetAddress" value={streetAddress} onChange={(e) => setStreetAddress(e.target.value)} required autoComplete="street-address" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                              </div>
                            </div>
                          </>
                        )}

                        <div className="sm:col-span-3">
                          <label htmlFor="email" className="block text-sm font-medium leading-6 text-white">Email</label>
                          <div className="mt-2">
                            <input type="email" name="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} required autoComplete="email" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="actuacao" className="block text-sm font-medium leading-6 text-white">Área de Atuação</label>
                          <div className="mt-2">
                            <select type="text" name="actuacao" id="actuacao" value={actuacao} onChange={(e) => setActuacao(e.target.value)} required autoComplete="off" className="block w-full rounded-md border-0 py-1.5 text-white shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6">
                              <option value=""></option>
                              {areasAtuacaoData.areasAtuacao.map((area, index) => (
                                <option key={index} value={area}>{area}</option>
                              ))}
                            </select>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="province" className="block text-sm font-medium leading-6 text-white">Província</label>
                          <div className="mt-2">
                            <select id="province" name="province" value={province} onChange={(e) => setProvince(e.target.value)} required autoComplete="address-level1" className="block w-full rounded-md border-0 py-1.5 bg-transparent text-white shadow-sm focus:ring-transparent sm:max-w-xs sm:text-sm sm:leading-6">
                              <option value=""></option>
                              {provinciasData.provincias.map((prov, index) => (
                                <option key={index} value={prov}>{prov}</option>
                              ))}
                            </select>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="telefone" className="block text-sm font-medium leading-6 text-white">Telefone</label>
                          <div className="mt-2">
                            <input type="tel" name="telefone" id="telefone" value={telefone} onChange={(e) => setTelefone(e.target.value)} required autoComplete="tel" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="profilePhoto" className="block text-sm font-medium leading-6 text-white">Foto de Perfil</label>
                          <div className="mt-2">
                            <input type="file" name="profilePhoto" id="profilePhoto" onChange={handleProfilePhotoChange} required className="block w-full text-white rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="coverPhoto" className="block text-sm font-medium leading-6 text-white">Foto de Capa</label>
                          <div className="mt-2">
                            <input type="file" name="coverPhoto" id="coverPhoto" onChange={handleCoverPhotoChange} required className="block w-full text-white rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="mt-6 flex items-center justify-end gap-x-6">
                    <button type="button" className="text-sm font-semibold leading-6 text-white">Cancelar</button>
                    <button type="submit" className="rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600">Salvar</button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Modal de sucesso */}
      {showSuccessModal && (
        <div className={styles.successModal}>
          <div className={styles.modalContent}>
            <p>Perfil atualizado com sucesso!</p>
            <button onClick={handleCloseSuccessModal} className={styles.closeButton}>Fechar</button>
          </div>
        </div>
      )}

      {/* Modal de erro */}
      {showErrorModal && (
        <div className={styles.errorModal}>
          <div className={styles.modalContent}>
            <p>Erro ao atualizar o perfil. Tente novamente.</p>
            <button onClick={handleCloseErrorModal} className={styles.closeButton}>Fechar</button>
          </div>
        </div>
      )}
    </>
  );
}
