import React, { useState } from 'react';
import { PrincipalPage } from '../../layout/principalPage';
import { FaPaperPlane } from 'react-icons/fa';

export function ChatPage() {
  // Estado inicial das mensagens no chat
  const [messages, setMessages] = useState([
    { id: 1, text: 'Hello!', sender: 'User1' },
    { id: 2, text: 'Hi, how are you?', sender: 'CurrentUser' },
    // Adicione mais mensagens conforme necessário
  ]);

  // Estado para a nova mensagem
  const [newMessage, setNewMessage] = useState('');

  // Função para enviar uma nova mensagem
  const handleSendMessage = () => {
    if (newMessage.trim() !== '') {
      setMessages([...messages, { id: messages.length + 1, text: newMessage, sender: 'CurrentUser' }]);
      setNewMessage('');
    }
  };

  return (
    <PrincipalPage>
      <div className="container mx-auto p-4">
        <h1 className="text-2xl font-bold mb-4 text-white">Chat</h1>
        <div className="bg-gray-800 p-4 rounded-md shadow-md flex flex-col h-96">
          {/* Área de mensagens */}
          <div className="flex-grow overflow-y-auto mb-4">
            {messages.map(message => (
              <div key={message.id} className={`mb-2 p-2 rounded-md ${message.sender === 'CurrentUser' ? 'bg-blue-500 text-white self-end' : 'bg-gray-700 text-white self-start'}`}>
                <p>{message.text}</p>
                <span className="text-sm text-gray-300">{message.sender}</span>
              </div>
            ))}
          </div>
          {/* Campo de entrada para nova mensagem */}
          <div className="flex items-center">
            <input
              type="text"
              placeholder="Digite sua mensagem..."
              className="flex-grow p-2 rounded-l-md bg-gray-700 text-white"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
            />
            <button onClick={handleSendMessage} className="bg-blue-500 p-2 rounded-r-md text-white">
              <FaPaperPlane />
            </button>
          </div>
        </div>
      </div>
    </PrincipalPage>
  );
}
