import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import styles from './Register.module.css'; // Estilos específicos para o componente
import successGif from '../../assets/img/icons8-sucesso.gif'; // Imagem de sucesso
import errorGif from '../../assets/img/icons8-erro.gif'; // Imagem de erro

// Importar os arquivos JSON para preencher os dados
import provinciasData from '../../JSON/provincias.json';
import areasAtuacaoData from '../../JSON/areasAtuacao.json';

export function Register() {
  // Estados para armazenar os dados do formulário e controle dos modais
  const [gestorName, setGestorName] = useState('');
  const [password, setPassword] = useState('');
  const [nameCompany, setNameCompany] = useState('');
  const [email, setEmail] = useState('');
  const [province, setProvince] = useState('');
  const [streetAddress, setStreetAddress] = useState('');
  const [nifEmpresa, setNifEmpresa] = useState('');
  const [actuacao, setActuacao] = useState('');
  const [telefone, setTelefone] = useState('');
  const [showSuccessModal, setShowSuccessModal] = useState(false); // Estado para controlar exibição do modal de sucesso
  const [showErrorModal, setShowErrorModal] = useState(false); // Estado para controlar exibição do modal de erro
  const [userType, setUserType] = useState(''); // Estado para armazenar o tipo de usuário (empresa ou empreendedor)

  // Função para fechar o modal de sucesso
  const handleCloseSuccessModal = () => setShowSuccessModal(false);

  // Função para fechar o modal de erro
  const handleCloseErrorModal = () => setShowErrorModal(false);

  // Função para lidar com o envio do formulário de registro
  const handleRegister = (e) => {
    e.preventDefault(); // Evita o comportamento padrão de submissão do formulário

    // Objeto com os dados do usuário a serem enviados
    const user = {
      gestorName,
      password,
      nameCompany,
      email,
      province,
      streetAddress,
      nifEmpresa,
      actuacao,
      telefone
    };

    // Requisição POST para o servidor com os dados do usuário
    axios.post('http://localhost:3001/register', user)
      .then(response => {
        setShowSuccessModal(true); // Exibe o modal de sucesso
        setTimeout(() => {
          setShowSuccessModal(false); // Fecha o modal de sucesso após 3 segundos
          // Aqui você pode redirecionar para a página de login ou fazer outra ação após o registro
        }, 3000);
      })
      .catch(error => {
        setShowErrorModal(true); // Exibe o modal de erro
        setTimeout(() => {
          setShowErrorModal(false); // Fecha o modal de erro após 2 segundos
        }, 2000);
      });
  };

  // Função para renderizar campos específicos dependendo do tipo de usuário selecionado
  const renderFormFields = () => {
    if (userType === 'empresa') {
      return (
        <>
          {/* Campos específicos para empresa */}
          <div className="sm:col-span-3">
            <label htmlFor="nameCompany" className="block text-sm font-medium leading-6 text-white">Nome da Empresa</label>
            <div className="mt-2">
              <input type="text" name="nameCompany" id="nameCompany" value={nameCompany} onChange={(e) => setNameCompany(e.target.value)} required autoComplete="organization" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
            </div>
          </div>
          <div className="sm:col-span-3">
            <label htmlFor="streetAddress" className="block text-sm font-medium leading-6 text-white">Endereço da Empresa</label>
            <div className="mt-2">
              <input type="text" name="streetAddress" id="streetAddress" value={streetAddress} onChange={(e) => setStreetAddress(e.target.value)} required autoComplete="street-address" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
            </div>
          </div>
          <div className="sm:col-span-3">
            <label htmlFor="nifEmpresa" className="block text-sm font-medium leading-6 text-white">NIF da Empresa</label>
            <div className="mt-2">
              <input type="text" name="nifEmpresa" id="nifEmpresa" value={nifEmpresa} onChange={(e) => setNifEmpresa(e.target.value)} minLength={14} maxLength={14} required autoComplete="tax-id" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
            </div>
          </div>
        </>
      );
    } else if (userType === 'empreendedor') {
      return (
        <>
          {/* Campos específicos para empreendedor */}
          <div className="sm:col-span-3">
            <label htmlFor="actuacao" className="block text-sm font-medium leading-6 text-white">Área de Atuação</label>
            <div className="mt-2">
              <select id="actuacao" name="actuacao" value={actuacao} onChange={(e) => setActuacao(e.target.value)} required className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm focus:ring-transparent sm:text-sm sm:leading-6">
                <option value=""></option>
                {areasAtuacaoData.areasAtuacao && areasAtuacaoData.areasAtuacao.map((area, index) => (
                  <option key={index} value={area}>{area}</option>
                ))}
              </select>
            </div>
          </div>
        </>
      );
    }
    return null;
  };

  return (
    <>
      <section className={styles.containerLogin}>
        <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8 bg-gray-900 opacity-90">
          <div className="max-w-3xl mx-auto px-4">
            <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8">
              <div className="mt-10 sm:mx-auto sm:w-full">
                <form onSubmit={handleRegister}>
                  <div className="space-y-12">
                    <div className="border-b border-gray-900/10 pb-12">
                      <h1 className="text-base font-semibold leading-7 text-white">Faça o Registro da sua conta</h1>
                      <p className="mt-1 text-sm leading-6 text-white">Por favor, use um endereço permanente onde você possa receber as correspondências.</p>
                      <div className="mt-10 grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6">
                        {/* Seletor de tipo de usuário */}
                        <div className="sm:col-span-3">
                          <label htmlFor="userType" className="block text-sm font-medium leading-6 text-white">Tipo de Usuário</label>
                          <div className="mt-2">
                            <select id="userType" name="userType" value={userType} onChange={(e) => setUserType(e.target.value)} required className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm focus:ring-transparent sm:text-sm sm:leading-6">
                              <option value=""></option>
                              <option value="empresa">Empresa</option>
                              <option value="empreendedor">Empreendedor</option>
                            </select>
                          </div>
                        </div>
                        {/* Campos comuns a todos os tipos de usuário */}
                        <div className="sm:col-span-3">
                          <label htmlFor="gestorName" className="block text-sm font-medium leading-6 text-white">Nome do Representante</label>
                          <div className="mt-2">
                            <input type="text" name="gestorName" id="gestorName" value={gestorName} onChange={(e) => setGestorName(e.target.value)} required autoComplete="given-name" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="password" className="block text-sm font-medium leading-6 text-white">Palavra-passe</label>
                          <div className="mt-2">
                            <input type="password" name="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required autoComplete="new-password" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="email" className="block text-sm font-medium leading-6 text-white">Email</label>
                          <div className="mt-2">
                            <input type="email" name="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} required autoComplete="email" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="telefone" className="block text-sm font-medium leading-6 text-white">Telefone</label>
                          <div className="mt-2">
                            <input type="tel" name="telefone" id="telefone" value={telefone} onChange={(e) => setTelefone(e.target.value)} required autoComplete="tel" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="province" className="block text-sm font-medium leading-6 text-white">Província</label>
                          <div className="mt-2">
                            <select id="province" name="province" value={province} onChange={(e) => setProvince(e.target.value)} required className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm focus:ring-transparent sm:text-sm sm:leading-6">
                              <option value=""></option>
                              {provinciasData.provincias && provinciasData.provincias.map((prov, index) => (
                                <option key={index} value={prov}>{prov}</option>
                              ))}
                            </select>
                          </div>
                        </div>
                        {/* Renderiza os campos específicos do tipo de usuário selecionado */}
                        {renderFormFields()}
                      </div>
                    </div>
                    {/* Botões de enviar e cancelar */}
                    <div className="mt-10 flex items-center justify-between">
                      <div className="flex items-center">
                        <button type="submit" className="w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                          Enviar
                        </button>
                        <Link to="/login" className="ml-4 bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                          Cancelar
                        </Link>
                      </div>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Modal de sucesso após o registro */}
      {showSuccessModal && (
        <div className="fixed z-10 inset-0 overflow-y-auto">
          <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 transition-opacity" aria-hidden="true">
              <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
            </div>
            {/* Centraliza o conteúdo do modal */}
            <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <div className="sm:flex sm:items-start">
                  <div className="mx-auto flex-shrink-0 flex items-center justify-center h-12 w-12 rounded-full bg-green-100 sm:mx-0 sm:h-10 sm:w-10">
                    <img src={successGif} alt="Ícone de sucesso" className="h-6 w-6"/>
                  </div>
                  <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left">
                    <h3 className="text-lg leading-6 font-medium text-gray-900">Registro concluído!</h3>
                    <div className="mt-2">
                      <p className="text-sm text-gray-500">Seu registro foi realizado com sucesso.</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Modal de erro após o registro */}
      {showErrorModal && (
        <div className="fixed z-10 inset-0 overflow-y-auto">
          <div className="flex items-end justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 transition-opacity" aria-hidden="true">
              <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
            </div>
            {/* Centraliza o conteúdo do modal */}
            <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>
            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <div className="sm:flex sm:items-start">
                  <div className="mx-auto flex-shrink-0 flex items-center justify-center h-12 w-12 rounded-full bg-red-100 sm:mx-0 sm:h-10 sm:w-10">
                    <img src={errorGif} alt="Ícone de erro" className="h-6 w-6"/>
                  </div>
                  <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left">
                    <h3 className="text-lg leading-6 font-medium text-gray-900">Erro no registro!</h3>
                    <div className="mt-2">
                      <p className="text-sm text-gray-500">Houve um erro ao tentar registrar. Por favor, tente novamente mais tarde.</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}
