import React, { useState } from "react";
import CarexLogo from "../../assets/img/carex_logo.png";
import { FaCamera, FaVideo, FaLine, FaCalendar } from 'react-icons/fa';

export function CreatePost({ onCreatePost }) {
  const [postText, setPostText] = useState("");
  const [postImage, setPostImage] = useState(null);
  const [postType, setPostType] = useState("");

  const handleCreatePost = () => {
    if (postText.trim() !== "" || postImage) {
      onCreatePost({ text: postText, image: postImage, type: postType });
      setPostText("");
      setPostImage(null);
      setPostType("");
    }
  };

  const handleImageChange = (e) => {
    setPostImage(URL.createObjectURL(e.target.files[0]));
  };

  return (
    <div className="bg-gray-900 border border-gray-700 rounded-md p-4 mb-4">
      <div className="second-col-section-home flex items-center mb-4 bg-gray-900">
        <img src={CarexLogo} alt="" className="w-14 h-14 rounded-full mr-4" />
        <input
          type="text"
          placeholder='Cria uma publicação'
          className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
          value={postText}
          onChange={(e) => setPostText(e.target.value)}
        />
        <button onClick={handleCreatePost} className="ml-2 bg-blue-500 text-white px-4 py-2 rounded">Postar</button>
      </div>
      <div className="page-home-uploads-section justify-between mb-4">
        <ul className="flex justify-between text-white font-semibold">
          <li className="flex items-center mr-1">
            <label htmlFor="file-upload" className="flex items-center cursor-pointer">
              <FaCamera className="mr-1" /> Mídias
            </label>
            <input
              id="file-upload"
              type="file"
              accept="image/*"
              className="hidden"
              onChange={handleImageChange}
            />
          </li>
          <li className="flex items-center px-2 cursor-pointer" onClick={() => setPostType("Meet")}><FaVideo className="mr-1" /> Meet</li>
          <li className="flex items-center px-2 cursor-pointer" onClick={() => setPostType("Startup")}><FaLine className="mr-1" /> StartUp</li>
          <li className="flex items-center cursor-pointer" onClick={() => setPostType("Evento")}><FaCalendar className="mr-1" /> Evento</li>
        </ul>
      </div>
      {postImage && <img src={postImage} alt="Preview" className="w-full mb-4" />}
    </div>
  );
}
