import React, { useState } from 'react';
import { PrincipalPage } from '../../layout/principalPage';
import TimelineHeader from "../../components/TimelineHeader/TimelineHeader";
import {Intro} from '../../components/Intro/intro';
import {CreatePost} from '../../components/CreatePost/CreatePost';
import {MainPost} from "../../components/MainPost/MainPost";
import {Posts} from "../../components/CreatePost/Posts";

export function UserProfile() {
  // Lista de posts
  const [posts, setPosts] = useState([]);

  // Função para adicionar um novo post
  const handleCreatePost = (newPost) => {
    setPosts([newPost, ...posts]);
  };

  // Função para lidar com curtidas nos posts
  const handleLikePost = (postId) => {
    const updatedPosts = posts.map(post => {
      if (post.id === postId) {
        return { ...post, isLiked: !post.isLiked, likes: post.likes + (post.isLiked ? -1 : 1) };
      }
      return post;
    });
    setPosts(updatedPosts);
  };

  // Função para adicionar um comentário a um post
  const handleCommentPost = (postId, comment) => {
    const updatedPosts = posts.map(post => {
      if (post.id === postId) {
        return { ...post, comments: [...post.comments, comment] };
      }
      return post;
    });
    setPosts(updatedPosts);
  };

  // Função para deletar um post
  const handleDeletePost = (postId) => {
    const updatedPosts = posts.filter(post => post.id !== postId);
    setPosts(updatedPosts);
  };

  return (
    <PrincipalPage>
      <TimelineHeader />
      <div className="flex-col md:grid grid-cols-12 pt-4 gap-4 z-0 pb-56">
        <div className="mb-5 md:col-span-4 col-start-1 row-start-1 space-y-4">
          <Intro />
        </div>
        <div className="flex-row row-start-1 col-span-12 col-start-5 space-y-4">
          <CreatePost onCreatePost={handleCreatePost} />
          <Posts />
          {/* Renderizar cada post */}
          {posts.map(post => (
            <MainPost
              key={post.id}
              post={post}
              onLike={handleLikePost}
              onComment={handleCommentPost}
              onDelete={handleDeletePost}
            />
          ))}
        </div>
      </div>
    </PrincipalPage>
  );
}
