import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Logo } from '../../components/logo';
import styles from './LoginPage.module.css'; 
import successGif from '../../assets/img/icons8-sucesso.gif'; 
import errorGif from '../../assets/img/icons8-erro.gif'; 

export function Login() {
  // ... (restante do cÃ³digo)

  return (
    <>
      {/* SeÃ§Ã£o de login */}
      <section className={styles.containerLogin}>
        <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8 bg-gray-900 opacity-90">
          <div className="max-w-3xl mx-auto px-4">
            <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8">
              <div className="sm:mx-auto sm:w-full sm:max-w-sm text-center">
                <Logo className={styles.logo} /> 
                <h2 className="mt-10 text-center text-2xl font-bold leading-9 tracking-tight text-white">
                  FaÃ§a login em sua conta
                </h2>
              </div>

              <div className="mt-10 sm:mx-auto sm:w-full sm:max-w-sm">
                {/* FormulÃ¡rio de login - centralizado horizontalmente */}
                <form onSubmit={handleLogin} className="space-y-4" style={{ maxWidth: '400px', margin: '0 auto' }}> 
                  {/* Campo de email */}
                  <div>
                    <label htmlFor="email" className="block text-sm font-medium leading-6 text-white">EndereÃ§o Email</label>
                    <div>
                      <input
                        id="email"
                        type="email"
                        autoComplete="email"
                        required
                        className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                      />
                    </div>
                  </div>
                  {/* Campo de senha */}
                  <div>
                    <label htmlFor="password" className="block text-sm font-medium leading-6 text-white">Palavra-passe</label>
                    <div>
                      <input
                        id="password"
                        type="password"
                        autoComplete="current-password"
                        required
                        className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                      />
                    </div>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-white"><Link to="/forgotpassword">Esqueceu a palavra-passe?</Link></span>
                  </div>
                  {/* BotÃ£o de login */}
                  <div>
                    <button
                      type="submit"
                      className="flex w-full justify-center rounded-md bg-indigo-600 px-3 py-1.5 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
                      disabled={loading}
                    >
                      {loading ? 'Carregando...' : 'Entrar'}
                    </button>
                  </div>
                  {/* ExibiÃ§Ã£o de erro */}
                  {error && (
                    <div className="text-red-500 text-sm text-center">
                      <p>{error}</p>
                    </div>
                  )}
                  {/* Link para registro */}
                  <p className="mt-10 text-center text-sm text-white">
                    NÃ£o tens uma conta?{' '}
                    <Link to="/register" className="font-semibold leading-6 text-indigo-600 hover:text-indigo-500">FaÃ§a o seu Registro</Link>
                  </p>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
