                  </div>
                  <div className="mt-6">
                    <label htmlFor="areaAtuacao" className="block text-sm font-medium leading-5 text-gray-700">Área de Atuação</label>
                    <input id="areaAtuacao" name="areaAtuacao" type="text" autoComplete="organization-title" required value={areaAtuacao} onChange={(e) => setAreaAtuacao(e.target.value)} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:shadow-outline-blue focus:border-blue-300 sm:text-sm sm:leading-5"/>
                  </div>
                </div>

                {/* Campos específicos para empresa */}
                {userType === 'empresa' && (
                  <div className="pt-8">
                    <div>
                      <label htmlFor="nifEmpresa" className="block text-sm font-medium leading-5 text-gray-700">NIF da Empresa</label>
                      <input id="nifEmpresa" name="nifEmpresa" type="text" autoComplete="tax-id" required value={nifEmpresa} onChange={(e) => setNifEmpresa(e.target.value)} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:shadow-outline-blue focus:border-blue-300 sm:text-sm sm:leading-5"/>
