                      <input id="bi" name="bi" type="text" autoComplete="organization" required value={bi} onChange={(e) => setBi(e.target.value)} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:shadow-outline-blue focus:border-blue-300 sm:text-sm sm:leading-5"/>
