import React, { useState } from "react";
import CarexLogo from "../../assets/img/carex_logo.png";
import { FaThumbsUp, FaShare, FaChevronCircleRight } from 'react-icons/fa';
import { Menu, MenuButton, MenuList, MenuItem } from '@reach/menu-button';


export function MainPost({ post, onLike, onComment, onDelete }) {
  const [commentText, setCommentText] = useState("");

  const handleAddComment = () => {
    if (commentText.trim() !== "") {
      onComment(post.id, commentText);
      setCommentText("");
    }
  };

  const lastCommentAuthor = post.comments.length > 0 ? post.comments[post.comments.length - 1].author : 'Nvuala Carvalho';

  return (
    <div className="containerPost bg-gray-900 border border-gray-700 rounded-md p-4 mb-4">
      <div className="whoViewdPost flex justify-between text-gray-400 ">
        <span className='whoViewdPostName cursor-pointer'>
          <span className="text-white font-semibold">{lastCommentAuthor}</span> comentou isto
        </span>
        <Menu>
          <MenuButton className="whoViewdPostDots text-white cursor-pointer">...</MenuButton>
          <MenuList className="bg-gray-800 text-white">
            <MenuItem onSelect={() => onDelete(post.id)}>Eliminar Post</MenuItem>
            <MenuItem onSelect={() => alert('Outra opção')}>Outra opção</MenuItem>
          </MenuList>
        </Menu>
      </div>
      <div className="hr-line w-full h-px bg-gray-600 my-1"></div>
      <div className="userPostDetails flex items-center mb-1">
        <img src={CarexLogo} alt="" className="w-12 h-12 rounded-full" />
        <div className="userPostMainDetails ml-4">
          <span className="userDetailsNamePost text-white font-semibold cursor-pointer">Carex Angola</span>
          <span className="userDetails-about text-gray-400 text-sm"> Despachante e Transportadora</span>
        </div>
      </div>
      <div className="mainFeedPost text-white mb-2 text-justify">
        {post.type && <span className="block mb-2 text-blue-400 font-semibold">{post.type}</span>}
        <p className="mb-4">{post.text}</p>
        {post.image && <img src={post.image} alt="Post" className="w-full mb-4" />}
      </div>
      <div className="flex justify-evenly items-center mb-4">
        <button onClick={() => onLike(post.id)} className={`flex items-center ${post.isLiked ? 'text-red-400' : 'text-blue-400'}`}>
          <FaThumbsUp className="mr-2" /> {post.isLiked ? 'Descurtir' : 'Curtir'} {post.likes}
        </button>
        <button className="flex items-center text-blue-400"><FaShare className="mr-2" /> Compartilhar</button>
      </div>
      <div className="bg-gray-800 rounded-md p-2 mb-2 flex justify-between">
        <input
          type="text"
          placeholder='Comentar...'
          className="w-full h-10 rounded-full pl-4 border border-gray-600 bg-gray-600 text-white"
          value={commentText}
          onChange={(e) => setCommentText(e.target.value)}
        />
        <button onClick={handleAddComment} className='text-blue-400 text-3xl ml-2'>
          <FaChevronCircleRight />
        </button>
      </div>
      {post.comments.slice().reverse().map((comment, index) => (
        <div key={index} className="bg-gray-800 rounded-md p-2 mb-2">
          <div className="flex justify-between text-gray-400 mb-2 text-sm">
            <span className="text-white font-bold">{comment.author}</span>
            <button className="text-blue-400"><FaThumbsUp className="mr-2" /></button>
          </div>
          <p className="text-white text-sm">{comment.text}</p>
        </div>
      ))}
    </div>
  );
}
