import React, { useState } from 'react';
import { PrincipalPage } from '../../layout/principalPage';
import "./userpage.css";
import Carex1 from "../../assets/img/carex.png";
import CarexLogo from "../../assets/img/carex_logo.png";
import { CreatePost } from '../../components/CreatePost/CreatePost';
import { MainPost } from '../../components/MainPost/MainPost';

export function UserPage() {
  const [posts, setPosts] = useState([
    {
      id: 1,
      text: "Postagem de outro usuário sobre tecnologia.",
      image: "",
      type: "Tecnologia",
      likes: 10,
      comments: [],
      isLiked: false,
    },
    {
      id: 2,
      text: "Postagem de outro usuário sobre startups.",
      image: "",
      type: "StartUp",
      likes: 5,
      comments: [],
      isLiked: false,
    }
    // Adicione mais postagens de exemplo conforme necessário
  ]);

  const [filterType, setFilterType] = useState('All');

  const handleCreatePost = ({ text, image, type }) => {
    const newPost = {
      id: posts.length + 1,
      text,
      image,
      type,
      likes: 0,
      comments: [],
      isLiked: false,
    };
    setPosts((prevPosts) => [newPost, ...prevPosts]);
  };

  const handleLikePost = (postId) => {
    setPosts((prevPosts) =>
      prevPosts.map(post => 
        post.id === postId ? { ...post, isLiked: !post.isLiked, likes: post.isLiked ? post.likes - 1 : post.likes + 1 } : post
      )
    );
  };

  const handleCommentPost = (postId, commentText) => {
    setPosts((prevPosts) =>
      prevPosts.map(post => 
        post.id === postId ? { ...post, comments: [...post.comments, { author: "Current User", text: commentText }] } : post
      )
    );
  };

  const handleDeletePost = (postId) => {
    setPosts((prevPosts) => prevPosts.filter(post => post.id !== postId));
  };

  const filteredPosts = filterType === 'All' ? posts : posts.filter(post => post.type === filterType);

  return (
    <PrincipalPage>
      <div className="home-page-cards flex flex-col lg:flex-row justify-between rounded-md">
        <div className="home-page-col-one bg-gray-800 rounded-md hidden lg:block w-full lg:w-1/5 h-96">
          <div className="col-card-one-img1 mb-4">
            <img src={Carex1} alt="Carex" className="w-full h-32 object-cover"/>
          </div>
          <div className="col-card-one-img2 relative flex justify-center items-center mb-4">
            <img src={CarexLogo} alt="Carex Logo" className="w-20 h-20 rounded-full absolute -top-10"/>
          </div>
          <div className="company-name text-center text-white font-bold mt-5 mb-1">
            <h1>CAREX ANGOLA</h1>
          </div>
          <div className="company-description text-center text-white mb-2">
            <p>Importação e exportação de produtos diversos</p>
          </div>
          <div className="hr-line w-full h-px bg-gray-600 my-2"></div>
          <div className="profile-views text-white">
            <div className="flex justify-between text-blue-400 mb-2 px-2">
              <span>Visualização do Perfil</span>
              <span>40</span>
            </div>
            <div className="flex justify-between text-blue-400 px-2 hover:cursor-pointer">
              <span>Conexões</span>
              <span>60</span>
            </div>
          </div>
          <div className="hr-line w-full h-px bg-gray-600 my-2"></div>
          <div className="flex justify-center text-blue-400 mb-2 px-2 hover:cursor-pointer">
            <span>Dashboard de Análise</span>
          </div>
        </div>

        <div className="home-page-col-two w-full lg:w-3/5 bg-gray-900 flex flex-col p-4">
          <CreatePost onCreatePost={handleCreatePost} />
          {filteredPosts.map(post => (
            <MainPost 
              key={post.id} 
              post={post} 
              onLike={handleLikePost} 
              onComment={handleCommentPost} 
              onDelete={handleDeletePost}
            />
          ))}
        </div>

        <div className="home-page-col-three bg-gray-800 rounded-md hidden lg:block w-full lg:w-1/5 h-96 p-4">
          <div className="text-white text-lg font-bold mb-4">Filtrar Postagens</div>
          <div className="flex flex-col space-y-2">
            <button 
              className={`py-2 px-4 rounded ${filterType === 'All' ? 'bg-blue-500 text-white' : 'bg-gray-700 text-gray-300'} hover:bg-blue-500 hover:text-white`}
              onClick={() => setFilterType('All')}
            >
              Todos
            </button>
            <button 
              className={`py-2 px-4 rounded ${filterType === 'StartUp' ? 'bg-blue-500 text-white' : 'bg-gray-700 text-gray-300'} hover:bg-blue-500 hover:text-white`}
              onClick={() => setFilterType('StartUp')}
            >
              StartUp
            </button>
            <button 
              className={`py-2 px-4 rounded ${filterType === 'Tecnologia' ? 'bg-blue-500 text-white' : 'bg-gray-700 text-gray-300'} hover:bg-blue-500 hover:text-white`}
              onClick={() => setFilterType('Tecnologia')}
            >
              Tecnologia
            </button>
            <button 
              className={`py-2 px-4 rounded ${filterType === 'Negócios' ? 'bg-blue-500 text-white' : 'bg-gray-700 text-gray-300'} hover:bg-blue-500 hover:text-white`}
              onClick={() => setFilterType('Negócios')}
            >
              Negócios
            </button>
          </div>
        </div>
      </div>
    </PrincipalPage>
  );
}
