import React, { useState, useEffect } from "react";
import axios from "axios";

export function MainPost() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    async function fetchPosts() {
      try {
        const response = await axios.get("http://localhost:3001/posts");
        setPosts(response.data);
      } catch (error) {
        console.error("Erro ao buscar posts:", error);
      }
    }
    fetchPosts();
  }, []);

  return (
    <div className="main-post-container bg-gray-900 border border-gray-700 rounded-md p-4">
      {posts.map((post) => (
        <div key={post.id} className="post-item mb-4">
          <div className="user-info flex items-center mb-1">
            <img
              src={post.profileImage}
              alt="User"
              className="w-12 h-12 rounded-full"
            />
            <div className="user-details ml-4">
              <span className="user-name text-white font-semibold cursor-pointer">
                {post.userName}
              </span>
              <span className="user-occupation text-gray-400 text-sm">
                {post.occupation}
              </span>
            </div>
          </div>
          <div className="post-content text-white mb-2">
            <p>{post.content}</p>
            {post.imageUrl && (
              <img src={post.imageUrl} alt="Post" className="w-full" />
            )}
          </div>
          <div className="post-actions flex justify-between items-center">
            <button className="text-blue-400 text-sm">Curtir</button>
            <button className="text-blue-400 text-sm">Compartilhar</button>
          </div>
        </div>
      ))}
    </div>
  );
}
