import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './Register.module.css';
import sucessgif from '../../assets/img/icons8-sucesso.gif';
import errogif from '../../assets/img/icons8-erro.gif';
import provinciasData from '../../JSON/provincias.json';
import areasAtuacaoData from '../../JSON/areasAtuacao.json';

export function Register() {
  const [userType, setUserType] = useState('');
  const [gestorName, setGestorName] = useState('');
  const [password, setPassword] = useState('');
  const [nameCompany, setNameCompany] = useState('');
  const [email, setEmail] = useState('');
  const [province, setProvince] = useState('');
  const [streetAddress, setStreetAddress] = useState('');
  const [nifEmpresa, setNifEmpresa] = useState('');
  const [actuacao, setActuacao] = useState('');
  const [telefone, setTelefone] = useState('');
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [showErrorModal, setShowErrorModal] = useState(false);

  const handleCloseSuccessModal = () => setShowSuccessModal(false);
  const handleCloseErrorModal = () => setShowErrorModal(false);

  useEffect(() => {
    console.log(provinciasData.provincias);
  }, []);

  const handleRegister = (e) => {
    e.preventDefault();

    let user = {};
    if (userType === 'empresa') {
      user = {
        gestorName,
        password,
        nameCompany,
        email,
        province,
        streetAddress,
        nifEmpresa,
        actuacao,
        telefone,
      };
    } else if (userType === 'empreendedor') {
      user = {
        empreendedorName: gestorName,
        password,
        empreendedorStreetAddress: streetAddress,
        empreendedorActuacao: actuacao,
        empreendedorTelefone: telefone,
      };
    }

    axios.post('http://localhost:3001/register', user)
      .then(response => {
        setShowSuccessModal(true);
        setTimeout(() => {
          setShowSuccessModal(false);
        }, 3000);
      })
      .catch(error => {
        setShowErrorModal(true);
        setTimeout(() => {
          setShowErrorModal(false);
        }, 2000);
      });
  };

  return (
    <>
      <section className={styles.containerLogin}>
        <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8 bg-gray-900 opacity-90">
          <div className="max-w-3xl mx-auto px-4">
            <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8">
              <div className="mt-10 sm:mx-auto sm:w-full">
                <div className="mb-8">
                  <label htmlFor="userType" className="block text-sm font-medium leading-8 text-xl text-white">Tipo de Usuário</label>
                  <div className="mt-2">
                    <select id="userType" name="userType" value={userType} onChange={(e) => setUserType(e.target.value)} required className="block w-full rounded-md border-0 py-1.5 bg-transparent text-white shadow-sm focus:ring-transparent text-white sm:max-w-xs  sm:text-sm sm:leading-6">
                      <option value=""></option>
                      <option value="empresa">Empresa</option>
                      <option value="empreendedor">Empreendedor</option>
                    </select>
                  </div>
                </div>
                {userType && (
                  <form onSubmit={handleRegister}>
                    <div className="space-y-12">
                      <div className="border-b border-gray-900/10 pb-12">
                        <h1 className="text-base font-semibold leading-7 text-white">Faça o Registro da sua conta</h1>
                        <p className="mt-1 text-sm leading-6 text-white">Por favor, use um endereço permanente onde você possa receber as correspondências.</p>
                        <div className="mt-10 grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6">
                          {userType === 'empresa' && (
                            <>
                              <div className="sm:col-span-3">
                                <label htmlFor="gestorName" className="block text-sm font-medium leading-6 text-white">Nome do Representante</label>
                                <div className="mt-2">
                                  <input type="text" name="gestorName" id="gestorName" value={gestorName} onChange={(e) => setGestorName(e.target.value)} required autoComplete="given-name" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                                </div>
                              </div>
                              <div className="sm:col-span-3">
                                <label htmlFor="password" className="block text-sm font-medium leading-6 text-white">Palavra-passe</label>
                                <div className="mt-2">
                                  <input type="password" name="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required minLength={8} autoComplete="new-password" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                                </div>
                              </div>
                              <div className="sm:col-span-3">
                                <label htmlFor="nameCompany" className="block text-sm font-medium leading-6 text-white">Nome da Empresa</label>
                                <div className="mt-2">
                                  <input type="text" name="nameCompany" id="nameCompany" value={nameCompany} onChange={(e) => setNameCompany(e.target.value)} required autoComplete="organization" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                                </div>
                              </div>
                              <div className="sm:col-span-3">
                                <label htmlFor="streetAddress" className="block text-sm font-medium leading-6 text-white">Endereço da Empresa</label>
                                <div className="mt-2">
                                  <input type="text" name="streetAddress" id="streetAddress" value={streetAddress} onChange={(e) => setStreetAddress(e.target.value)} required autoComplete="street-address" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                                </div>
                              </div>
                              <div className="sm:col-span-3">
                                <label htmlFor="actuacao" className="block text-sm font-medium leading-6 text-white">Área de Atuação</label>
                                <div className="mt-2">
                                  <select name="actuacao" id="actuacao" value={actuacao} onChange={(e) => setActuacao(e.target.value)} required autoComplete="off" className="block w-full rounded-md border-0 py-1.5 text-white shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6">
                                    <option value=""></option>
                                    {areasAtuacaoData.areasAtuacao.map((area, index) => (
                                      <option key={index} value={area}>{area}</option>
                                    ))}
                                  </select>
                                </div>
                              </div>
                              <div className="sm:col-span-3">
                                <label htmlFor="nifEmpresa" className="block text-sm font-medium leading-6 text-white">NIF da Empresa</label>
                                <div className="mt-2">
                                  <input type="text" name="nifEmpresa" id="nifEmpresa" value={nifEmpresa} onChange={(e) => setNifEmpresa(e.target.value)} required autoComplete="tax-id" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                                </div>
                              </div>
                            </>
                          )}
                          {userType === 'empreendedor' && (
                            <>
                              <div className="sm:col-span-3">
                                <label htmlFor="empreendedorName" className="block text-sm font-medium leading-6 text-white">Nome do Empreendedor</label>
                                <div className="mt-2">
                                  <input type="text" name="empreendedorName" id="empreendedorName" value={gestorName} onChange={(e) => setGestorName(e.target.value)} required autoComplete="given-name" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                                </div>
                              </div>
                              <div className="sm:col-span-3">
                                <label htmlFor="password" className="block text-sm font-medium leading-6 text-white">Palavra-passe</label>
                                <div className="mt-2">
                                  <input type="password" name="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required minLength={8} autoComplete="new-password" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                                </div>
                              </div>
                              <div className="sm:col-span-3">
                                <label htmlFor="empreendedorStreetAddress" className="block text-sm font-medium leading-6 text-white">Endereço do Empreendedor</label>
                                <div className="mt-2">
                                  <input type="text" name="empreendedorStreetAddress" id="empreendedorStreetAddress" value={streetAddress} onChange={(e) => setStreetAddress(e.target.value)} required autoComplete="street-address" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                                </div>
                              </div>
                              <div className="sm:col-span-3">
                                <label htmlFor="empreendedorActuacao" className="block text-sm font-medium leading-6 text-white">Área de Atuação</label>
                                <div className="mt-2">
                                  <select name="empreendedorActuacao" id="empreendedorActuacao" value={actuacao} onChange={(e) => setActuacao(e.target.value)} required autoComplete="off" className="block w-full rounded-md border-0 py-1.5 text-white shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6">
                                    <option value=""></option>
                                    {areasAtuacaoData.areasAtuacao.map((area, index) => (
                                      <option key={index} value={area}>{area}</option>
                                    ))}
                                  </select>
                                </div>
                              </div>
                              <div className="sm:col-span-3">
                                <label htmlFor="empreendedorTelefone" className="block text-sm font-medium leading-6 text-white">Telefone do Empreendedor</label>
                                <div className="mt-2">
                                  <input type="text" name="empreendedorTelefone" id="empreendedorTelefone" value={telefone} onChange={(e) => setTelefone(e.target.value)} required autoComplete="tel" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                                </div>
                              </div>
                            </>
                          )}
                          <div className="sm:col-span-3">
                            <button type="submit" className="flex w-full justify-center rounded-md bg-indigo-600 py-2 px-3 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600">Registrar</button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </form>
                )}
              </div>
            </div>
          </div>
        </div>
      </section>

      {showSuccessModal && (
        <div className={styles.modal}>
          <div className={styles.modalContent}>
            <img src={sucessgif} alt="Success" className={styles.modalImage} />
            <h2 className={styles.modalTitle}>Sucesso!</h2>
            <p className={styles.modalMessage}>Registro efetuado com sucesso!</p>
            <button onClick={handleCloseSuccessModal} className={styles.modalButton}>Fechar</button>
          </div>
        </div>
      )}

      {showErrorModal && (
        <div className={styles.modal}>
          <div className={styles.modalContent}>
            <img src={errogif} alt="Error" className={styles.modalImage} />
            <h2 className={styles.modalTitle}>Erro!</h2>
            <p className={styles.modalMessage}>Ocorreu um erro ao registrar. Por favor, tente novamente.</p>
            <button onClick={handleCloseErrorModal} className={styles.modalButton}>Fechar</button>
          </div>
        </div>
      )}
    </>
  );
}
