import React, { useState } from "react";
import axios from "axios";

export function CreatePost({ userId }) {
  const [content, setContent] = useState("");
  const [imageUrl, setImageUrl] = useState("");

  const handlePost = async () => {
    try {
      await axios.post("http://localhost:3001/posts", {
        userId,
        content,
        imageUrl,
      });
      console.log("Post criado com sucesso!");
      // Limpar campos após a criação do post
      setContent("");
      setImageUrl("");
    } catch (error) {
      console.error("Erro ao criar post:", error);
    }
  };

  return (
    <div className="create-post-container bg-gray-800 p-4 rounded-md mb-4">
      <textarea
        placeholder="Crie uma publicação..."
        value={content}
        onChange={(e) => setContent(e.target.value)}
        className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
      />
      <input
        type="text"
        placeholder="URL da imagem (opcional)"
        value={imageUrl}
        onChange={(e) => setImageUrl(e.target.value)}
        className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
      />
      <button
        onClick={handlePost}
        className="bg-blue-500 hover:bg-blue-600 text-white text-sm py-2 px-4 rounded-full mt-2"
      >
        Postar
      </button>
    </div>
  );
}
