import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import styles from './Register.module.css';
import sucessgif from '../../assets/img/icons8-sucesso.gif';
import errogif from '../../assets/img/icons8-erro.gif';
import provinciasData from '../../JSON/provincias.json';
import areasAtuacaoData from '../../JSON/areasAtuacao.json';

export function Register() {
  const [userType, setUserType] = useState(''); // Novo estado para o tipo de usuário
  const [gestorName, setGestorName] = useState('');
  const [password, setPassword] = useState('');
  const [nameCompany, setNameCompany] = useState('');
  const [email, setEmail] = useState('');
  const [province, setProvince] = useState('');
  const [streetAddress, setStreetAddress] = useState('');
  const [nifEmpresa, setNifEmpresa] = useState('');
  const [actuacao, setActuacao] = useState('');
  const [telefone, setTelefone] = useState('');
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [showErrorModal, setShowErrorModal] = useState(false);

  const handleCloseSuccessModal = () => setShowSuccessModal(false);
  const handleCloseErrorModal = () => setShowErrorModal(false);

  useEffect(() => {
    console.log(provinciasData.provincias);
  }, []);

  const handleRegister = (e) => {
    e.preventDefault();

    const user = {
      gestorName,
      password,
      nameCompany: userType === 'empresa' ? nameCompany : '',
      email,
      province,
      streetAddress: userType === 'empresa' ? streetAddress : '',
      nifEmpresa,
      actuacao,
      telefone
    };

    axios.post('http://localhost:3001/register', user)
      .then(response => {
        setShowSuccessModal(true);
        setTimeout(() => {
          setShowSuccessModal(false);
        }, 3000);
      })
      .catch(error => {
        setShowErrorModal(true);
        setTimeout(() => {
          setShowErrorModal(false);
        }, 2000);
      });
  };

  return (
    <>
      <section className={styles.containerLogin}>
        <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8 bg-gray-900 opacity-90">
          <div className="max-w-3xl mx-auto px-4">
            <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8">
              <div className="mt-10 sm:mx-auto sm:w-full">
                <form onSubmit={handleRegister}>
                  <div className="space-y-12">
                    <div className="border-b border-gray-900/10 pb-12">
                      <h1 className="text-base font-semibold leading-7 text-white">Faça o Registro da sua conta</h1>
                      <p className="mt-1 text-sm leading-6 text-white">Por favor, use um endereço permanente onde você possa receber as correspondências.</p>
                      <div className="mt-10 grid grid-cols-1 gap-x-6 gap-y-8 sm:grid-cols-6">
                        <div className="sm:col-span-3">
                          <label htmlFor="userType" className="block text-sm font-medium leading-6 text-white">Tipo de Usuário</label>
                          <div className="mt-2">
                            <select id="userType" name="userType" value={userType} onChange={(e) => setUserType(e.target.value)} required autoComplete="off" className="block w-full rounded-md border-0 py-1.5 bg-transparent text-white shadow-sm focus:ring-transparent sm:max-w-xs sm:text-sm sm:leading-6">
                              <option value="">Selecione</option>
                              <option value="empresa">Empresa</option>
                              <option value="empreendedor">Empreendedor</option>
                            </select>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="gestorName" className="block text-sm font-medium leading-6 text-white">Nome do Representante</label>
                          <div className="mt-2">
                            <input type="text" name="gestorName" id="gestorName" value={gestorName} onChange={(e) => setGestorName(e.target.value)} required autoComplete="given-name" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>
                        {userType === 'empresa' && (
                          <>
                            <div className="sm:col-span-3">
                              <label htmlFor="nameCompany" className="block text-sm font-medium leading-6 text-white">Nome da Empresa</label>
                              <div className="mt-2">
                                <input type="text" name="nameCompany" id="nameCompany" value={nameCompany} onChange={(e) => setNameCompany(e.target.value)} required autoComplete="organization" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                              </div>
                            </div>
                            <div className="col-span-full">
                              <label htmlFor="streetAddress" className="block text-sm font-medium leading-6 text-white">Endereço da Empresa</label>
                              <div className="mt-2">
                                <input type="text" name="streetAddress" id="streetAddress" value={streetAddress} onChange={(e) => setStreetAddress(e.target.value)} required autoComplete="street-address" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                              </div>
                            </div>
                          </>
                        )}
                        <div className="sm:col-span-3">
                          <label htmlFor="email" className="block text-sm font-medium leading-6 text-white">Endereço Email</label>
                          <div className="mt-2">
                            <input id="email" name="email" type="email" value={email} onChange={(e) => setEmail(e.target.value)} autoComplete="email" required className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="province" className="block text-sm font-medium leading-6 text-white">Província</label>
                          <div className="mt-2">
                            <select id="province" name="province" value={province} onChange={(e) => setProvince(e.target.value)} required autoComplete="address-level1" className="block w-full rounded-md border-0 py-1.5 bg-transparent text-white shadow-sm focus:ring-transparent sm:max-w-xs sm:text-sm sm:leading-6">
                              <option value=""></option>
                              {provinciasData.provincias.map((prov, index) => (
                                <option key={index} value={prov}>{prov}</option>
                              ))}
                            </select>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="nifEmpresa" className="block text-sm font-medium leading-6 text-white">NIF {userType === 'empresa' ? 'da Empresa' : 'Pessoal'}</label>
                          <div className="mt-2">
                            <input type="text" name="nifEmpresa" id="nifEmpresa" value={nifEmpresa} onChange={(e) => setNifEmpresa(e.target.value)} minLength={14} maxLength={14} required autoComplete="tax-id" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="actuacao" className="block text-sm font-medium leading-6 text-white">Área de Actuação</label>
                          <div className="mt-2">
                            <select id="actuacao" name="actuacao" value={actuacao} onChange={(e) => setActuacao(e.target.value)} required autoComplete="off" className="block w-full rounded-md border-0 py-1.5 bg-transparent text-white shadow-sm focus:ring-transparent sm:text-sm sm:leading-6">
                              <option value=""></option>
                              {areasAtuacaoData.areas.map((area, index) => (
                                <option key={index} value={area}>{area}</option>
                              ))}
                            </select>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="telefone" className="block text-sm font-medium leading-6 text-white">Telefone</label>
                          <div className="mt-2">
                            <input type="text" name="telefone" id="telefone" value={telefone} onChange={(e) => setTelefone(e.target.value)} minLength={9} maxLength={9} required autoComplete="tel" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>
                        <div className="sm:col-span-3">
                          <label htmlFor="password" className="block text-sm font-medium leading-6 text-white">Senha</label>
                          <div className="mt-2">
                            <input id="password" name="password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} autoComplete="current-password" required className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder:text-gray-400 focus:ring-transparent sm:text-sm sm:leading-6"/>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                  <div className="mt-6 flex items-center justify-end gap-x-6">
                    <button type="button" className="text-sm font-semibold leading-6 text-white">Cancelar</button>
                    <button type="submit" className="rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600">Registrar</button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </section>

      {showSuccessModal && (
        <div className={styles.modal}>
          <div className={styles.modalContent}>
            <img src={sucessgif} alt="Sucesso" className={styles.modalImage} />
            <h2 className={styles.modalTitle}>Sucesso!</h2>
            <p className={styles.modalMessage}>Registrado com sucesso!</p>
          </div>
        </div>
      )}

      {showErrorModal && (
        <div className={styles.modal}>
          <div className={styles.modalContent}>
            <img src={errogif} alt="Erro" className={styles.modalImage} />
            <h2 className={styles.modalTitle}>Erro!</h2>
            <p className={styles.modalMessage}>Ocorreu um erro no registro!</p>
          </div>
        </div>
      )}
    </>
  );
}
