import React, { useState } from "react";
import { FaEdit } from "react-icons/fa";
import Carex1 from "../../assets/img/carex.png";
import CarexLogo from "../../assets/img/carex_logo.png";
import axios from 'axios';
import provinciasData from '../../JSON/provincias.json';
import areasAtuacaoData from '../../JSON/areas.json';
import { PrincipalPage } from '../../layout/principalPage';

export function ProfileEdit() {
  const [userType, setUserType] = useState(''); // Estado para o tipo de usuário
    const [titleProfile, settitleProfile] = useState('');
  const [gestorName, setGestorName] = useState('');
  const [password, setPassword] = useState('');
  const [nameCompany, setNameCompany] = useState('');
  const [email, setEmail] = useState('');
  const [province, setProvince] = useState('');
  const [streetAddress, setStreetAddress] = useState('');
  const [nifEmpresa, setNifEmpresa] = useState('');
  const [actuacao, setActuacao] = useState('');
  const [telefone, setTelefone] = useState('');
  const [profilePhoto, setProfilePhoto] = useState(null);
  const [coverPhoto, setCoverPhoto] = useState(null);
  const [showSuccessModal, setShowSuccessModal] = useState(false); // Estado para controlar exibição do modal de sucesso
  const [showErrorModal, setShowErrorModal] = useState(false); // Estado para controlar exibição do modal de erro

  const handleProfilePhotoChange = (e) => {
    setProfilePhoto(e.target.files[0]);
  };

  const handleCoverPhotoChange = (e) => {
    setCoverPhoto(e.target.files[0]);
  };

  const handleCloseSuccessModal = () => setShowSuccessModal(false);
  const handleCloseErrorModal = () => setShowErrorModal(false);

  const handleUpdateProfile = (e) => {
    e.preventDefault();

    const user = {
      titleProfile,
      gestorName,
      password,
      nameCompany,
      email,
      province,
      streetAddress,
      nifEmpresa,
      actuacao,
      telefone,
      profilePhoto,
      coverPhoto,
    };

    axios.post('http://localhost:3001/updateProfile', user)
      .then(response => {
        setShowSuccessModal(true); // Exibe o modal de sucesso
        setTimeout(() => {
          setShowSuccessModal(false); // Fecha o modal de sucesso após 3 segundos
          // Aqui você pode redirecionar para a página de perfil ou fazer outra ação após a atualização
        }, 3000);
      })
      .catch(error => {
        setShowErrorModal(true); // Exibe o modal de erro
        setTimeout(() => {
          setShowErrorModal(false); // Fecha o modal de erro após 2 segundos
        }, 2000);
      });
  };

  return (
    <PrincipalPage>
    <section className=" max-width bg-gray-850 flex items-center justify-center">
      <div className="w-full mx-auto">
        <div className="relative h-96 rounded-lg mb-16">
          <img src={Carex1} className="object-cover w-full h-full rounded-lg" alt="cover" />
          <div className="absolute bottom-4 right-4">
            <label htmlFor="coverPhoto" className="cursor-pointer">
              <FaEdit className="text-white text-2xl" />
              <input type="file" id="coverPhoto" className="hidden" onChange={handleCoverPhotoChange} />
            </label>
          </div>
          <div className="absolute -bottom-16">
            <img src={CarexLogo} className="object-cover border-2 border-gray-850 w-40 h-40 rounded-full" alt="profile" />
            <div className="absolute bottom-0 right-0 p-2">
              <label htmlFor="profilePhoto" className="cursor-pointer">
                <FaEdit className="text-white text-2xl" />
                <input type="file" id="profilePhoto" className="hidden" onChange={handleProfilePhotoChange} />
              </label>
            </div>
          </div>
        </div>

        <div className="bg-gray-850 rounded-lg p-6 ">
          <form onSubmit={handleUpdateProfile}>
            <h1 className="text-xl lg:text-2xl font-semibold text-white mb-4">Editar Perfil</h1>
            <p className="text-sm text-gray-300 mb-6">Atualize as informações do seu perfil.</p>

            <div className="grid grid-cols-1 gap-y-8 gap-x-6 sm:grid-cols-2">
            <div>
                <label htmlFor="titleProfile" className="block text-sm font-medium leading-6 text-white">Titúlo do Perfil</label>
                <input type="text" name="tileProfile" id="titleProfile" value={titleProfile} onChange={(e) => settitleProfile(e.target.value)} required autoComplete="given-name" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder-gray-400 focus:ring-2 focus:ring-indigo-500 sm:text-sm sm:leading-6"/>
              </div>
              <div>
                <label htmlFor="gestorName" className="block text-sm font-medium leading-6 text-white">Nome do Representante</label>
                <input type="text" name="gestorName" id="gestorName" value={gestorName} onChange={(e) => setGestorName(e.target.value)} required autoComplete="given-name" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder-gray-400 focus:ring-2 focus:ring-indigo-500 sm:text-sm sm:leading-6"/>
              </div>
              <div>
                <label htmlFor="password" className="block text-sm font-medium leading-6 text-white">Palavra-passe</label>
                <input type="password" name="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required minLength={8} autoComplete="new-password" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder-gray-400 focus:ring-2 focus:ring-indigo-500 sm:text-sm sm:leading-6"/>
              </div>

              {/* Campos adicionais para empresas */}
              {userType === 'empresa' && (
                <>
                  <div>
                    <label htmlFor="nameCompany" className="block text-sm font-medium leading-6 text-white">Nome da Empresa</label>
                    <input type="text" name="nameCompany" id="nameCompany" value={nameCompany} onChange={(e) => setNameCompany(e.target.value)} required autoComplete="organization" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder-gray-400 focus:ring-2 focus:ring-indigo-500 sm:text-sm sm:leading-6"/>
                  </div>
                  <div>
                    <label htmlFor="streetAddress" className="block text-sm font-medium leading-6 text-white">Endereço da Empresa</label>
                    <input type="text" name="streetAddress" id="streetAddress" value={streetAddress} onChange={(e) => setStreetAddress(e.target.value)} required autoComplete="street-address" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder-gray-400 focus:ring-2 focus:ring-indigo-500 sm:text-sm sm:leading-6"/>
                  </div>
                </>
              )}

              {/* Campos para ambos os tipos de usuários */}
              <div>
                <label htmlFor="actuacao" className="block text-sm font-medium leading-6 text-white">Área de Atuação</label>
                <select type="text" name="actuacao" id="actuacao" value={actuacao} onChange={(e) => setActuacao(e.target.value)} required autoComplete="off" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder-gray-400 focus:ring-2 focus:ring-indigo-500 sm:text-sm sm:leading-6">
                  <option value=""></option>
                  {areasAtuacaoData.areasAtuacao.map((area, index) => (
                    <option key={index} value={area}>{area}</option>
                  ))}
                </select>
              </div>
              <div>
                <label htmlFor="province" className="block text-sm font-medium leading-6 text-white">Província</label>
                <select id="province" name="province" value={province} onChange={(e) => setProvince(e.target.value)} required autoComplete="address-level1" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder-gray-400 focus:ring-2 focus:ring-indigo-500 sm:text-sm sm:leading-6">
                  <option value=""></option>
                  {provinciasData.provincias.map((provincia, index) => (
                    <option key={index} value={provincia}>{provincia}</option>
                  ))}
                </select>
              </div>
              <div>
                <label htmlFor="email" className="block text-sm font-medium leading-6 text-white">Email</label>
                <input id="email" name="email" type="email" value={email} onChange={(e) => setEmail(e.target.value)} required autoComplete="email" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder-gray-400 focus:ring-2 focus:ring-indigo-500 sm:text-sm sm:leading-6"/>
              </div>
              <div>
                <label htmlFor="telefone" className="block text-sm font-medium leading-6 text-white">Telefone</label>
                <input type="tel" name="telefone" id="telefone" value={telefone} onChange={(e) => setTelefone(e.target.value)} required autoComplete="tel" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder-gray-400 focus:ring-2 focus:ring-indigo-500 sm:text-sm sm:leading-6"/>
              </div>
            </div>

            <div className="mt-8 flex justify-end">
              <button type="button" className="text-sm font-semibold leading-6 text-gray-400 hover:text-white">Cancelar</button>
              <button type="submit" className="ml-4 px-4 py-2 bg-indigo-600 text-white rounded-md shadow-sm hover:bg-indigo-500 focus:ring-2 focus:ring-indigo-500">Salvar</button>
            </div>
          </form>
        </div>
      </div>

      {/* Modal de sucesso */}
      {showSuccessModal && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center">
          <div className="bg-white p-6 rounded-md">
            <p className="text-green-600">Perfil atualizado com sucesso!</p>
            <button onClick={handleCloseSuccessModal} className="mt-4 px-4 py-2 bg-green-600 text-white rounded-md">Fechar</button>
          </div>
        </div>
      )}

      {/* Modal de erro */}
      {showErrorModal && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-75 flex items-center justify-center">
          <div className="bg-white p-6 rounded-md">
            <p className="text-red-600">Erro ao atualizar o perfil. Tente novamente.</p>
            <button onClick={handleCloseErrorModal} className="mt-4 px-4 py-2 bg-red-600 text-white rounded-md">Fechar</button>
          </div>
        </div>
      )}
    </section>
  </PrincipalPage>
  );
}
