import React, { useState } from 'react';
import axios from 'axios';

const areas = [
    'Tecnologia', 
    'Saúde', 
    'Educação', 
    'Finanças', 
    'Consultoria'
    // Adicione outras áreas conforme necessário
  ];

  const provincias = [
    'Luanda', 
    'Benguela', 
    'Huambo', 
    'Namibe', 
    'Malanje'
    // Adicione outras províncias conforme necessário
  ];
export function FormularioEmpreendedor() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    nome: '',
    genero: '',
    data_nascimento: '',
    bi: '',
    area_atuacao: '',
    provincia: '',
    telefone: '',
    endereco: '',
  });
  const [showSuccess, setShowSuccess] = useState(false);
  const [showError, setShowError] = useState(false);

  // Função para lidar com mudanças nos campos do formulário
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Função para lidar com o envio do formulário
  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:3001/register/empreendedor", formData);
      if (response.status === 201) {
        setShowSuccess(true);
        setShowError(false);
      } else {
        setShowError(true);
        setShowSuccess(false);
      }
    } catch (error) {
      setShowError(true);
      setShowSuccess(false);
    }
  };

  // Função para lidar com o cancelamento do registro
  const handleCancel = () => {
    setFormData({
      email: '',
      password: '',
      nome: '',
      genero: '',
      data_nascimento: '',
      bi: '',
      area_atuacao: '',
      provincia: '',
      telefone: '',
      endereco: '',
    });

    setShowSuccess(false);
    setShowError(false);
  };

  return (
    <form onSubmit={handleRegister} className="bg-gray-900 p-8 rounded-lg shadow-lg w-full max-w-4xl">
      <h2 className="text-2xl font-bold text-white mb-3">Registro de Empreendedor</h2>
      <div className="grid grid-cols-3 gap-4">
        {/* Campos do formulário */}
        <div className="col-span-2 mb-4">
          <label className="block text-gray-400 mb-2">Email*</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            required
          />
        </div>
        <div className="col-span-1 mb-4">
          <label className="block text-gray-400 mb-2">Senha*</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            required
          />
        </div>
        <div className="col-span-2 mb-4">
          <label className="block text-gray-400 mb-2">Nome*</label>
          <input
            type="text"
            name="nome"
            value={formData.nome}
            onChange={handleChange}
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            required
          />
        </div>
        <div className="col-span-1 mb-4">
          <label className="block text-gray-400 mb-2">Gênero*</label>
          <select
            name="genero"
            value={formData.genero}
            onChange={handleChange}
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            required
          >
            <option value="">Selecione</option>
            <option value="Masculino">Masculino</option>
            <option value="Feminino">Feminino</option>
          </select>
        </div>
        <div className="col-span-1 mb-4">
          <label className="block text-gray-400 mb-2">Data de Nascimento*</label>
          <input
            type="date"
            name="data_nascimento"
            value={formData.data_nascimento}
            onChange={handleChange}
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            required
          />
        </div>
        <div className="col-span-1 mb-4">
          <label className="block text-gray-400 mb-2">BI*</label>
          <input
            type="text"
            name="bi"
            value={formData.bi}
            onChange={handleChange}
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            required
          />
        </div>
        <div className="col-span-1 mb-4">
          <label className="block text-gray-400 mb-2">Área de Atuação*</label>
          <select
            name="area_atuacao"
            value={formData.area_atuacao}
            onChange={handleChange}
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            required
          >
            <option value="">Selecione</option>
            {areas.map((area, index) => (
              <option key={index} value={area}>{area}</option>
            ))}
          </select>
        </div>
        <div className="col-span-1 mb-4">
          <label className="block text-gray-400 mb-2">Província*</label>
          <select
            name="provincia"
            value={formData.provincia}
            onChange={handleChange}
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            required
          >
            <option value="">Selecione</option>
            {provincias.map((provincia, index) => (
              <option key={index} value={provincia}>{provincia}</option>
            ))}
          </select>
        </div>
        <div className="col-span-1 mb-4">
          <label className="block text-gray-400 mb-2">Telefone*</label>
          <input
            type="text"
            name="telefone"
            value={formData.telefone}
            onChange={handleChange}
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            required
          />
        </div>
        <div className="col-span-2 mb-4">
          <label className="block text-gray-400 mb-2">Endereço*</label>
          <input
            type="text"
            name="endereco"
            value={formData.endereco}
            onChange={handleChange}
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            required
          />
        </div>
      </div>
      <div className="mt-6 flex">
        <div className="px-4">
          <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            Registrar
          </button>
        </div>
        <div>
          <button
            type="button"
            onClick={handleCancel}
            className="bg-gray-700 hover:bg-gray-800 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            Cancelar
          </button>
        </div>
      </div>
      {showSuccess && (
        <div className="mt-4 text-green-500">
          Usuário registrado com sucesso!
        </div>
      )}
      {showError && (
        <div className="mt-4 text-red-500">
          Ocorreu um erro durante o registro. Por favor, tente novamente.
        </div>
      )}
    </form>
  );
}
