import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './Register.module.css';
import sucessgif from '../../assets/img/icons8-sucesso.gif';
import errogif from '../../assets/img/icons8-erro.gif';
import provinciasData from '../../JSON/provincias.json';
import areasAtuacaoData from '../../JSON/areasAtuacao.json';

export function Register() {
  const [userType, setUserType] = useState('');
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [address, setAddress] = useState('');
  const [province, setProvince] = useState('');
  const [areaActuacao, setAreaActuacao] = useState('');
  const [nif, setNif] = useState('');
  const [telefone, setTelefone] = useState('');
  const [nameCompany, setNameCompany] = useState('');
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [showErrorModal, setShowErrorModal] = useState(false);

  const handleCloseSuccessModal = () => setShowSuccessModal(false);
  const handleCloseErrorModal = () => setShowErrorModal(false);

  useEffect(() => {
    console.log(provinciasData.provincias);
  }, []);

  const handleRegister = (e) => {
    e.preventDefault();

    const user = {
      userType,
      name,
      password,
      address,
      province,
      areaActuacao,
      nif,
      telefone,
      nameCompany: userType === 'empresa' ? nameCompany : null,
    };

    axios.post('http://localhost:3001/register', user)
      .then(response => {
        setShowSuccessModal(true);
        setTimeout(() => {
          setShowSuccessModal(false);
        }, 3000);
      })
      .catch(error => {
        setShowErrorModal(true);
        setTimeout(() => {
          setShowErrorModal(false);
        }, 2000);
      });
  };

  return (
    <>
