import React, { useState } from 'react';
import axios from 'axios';
import { Dialog, Transition } from '@headlessui/react';
import { Link, useNavigate } from 'react-router-dom';

export function ForgotPassword() {
  const [email, setEmail] = useState('');
  const [code, setCode] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [message, setMessage] = useState(null);
  const navigate = useNavigate();

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleCodeChange = (e) => {
    setCode(e.target.value);
  };

  const handleNewPasswordChange = (e) => {
    setNewPassword(e.target.value);
  };

  const sendCode = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/forgot-password/send-code', { email });
      setMessage({ type: 'success', text: response.data.message });
      setIsOpen(true);
    } catch (error) {
      setMessage({ type: 'error', text: 'Erro ao enviar código' });
      setIsOpen(true);
    }
  };

  const resetPassword = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/forgot-password/reset', {
        email,
        code,
        newPassword,
      });
      setMessage({ type: 'success', text: response.data.message });
      setIsOpen(true);
      navigate('/login'); // Redirecione para a página de login
    } catch (error) {
      setMessage({ type: 'error', text: 'Erro ao redefinir a senha' });
      setIsOpen(true);
    }
  };

  const closeModal = () => {
    setIsOpen(false);
  };

  return (
    <div className="bg-gray-900 p-8 rounded-lg shadow-lg w-full max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold text-white mb-3">Redefinir Senha</h2>
      <form onSubmit={sendCode} className="grid gap-4">
        <div>
          <label className="block text-gray-400 mb-2">Email</label>
          <input
            type="email"
            name="email"
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            value={email}
            onChange={handleEmailChange}
            required
          />
        </div>
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          Enviar Código
        </button>
      </form>

      {code && (
        <form onSubmit={resetPassword} className="grid gap-4 mt-4">
          <div>
            <label className="block text-gray-400 mb-2">Código</label>
            <input
              type="text"
              name="code"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={code}
              onChange={handleCodeChange}
              required
            />
          </div>
          <div>
            <label className="block text-gray-400 mb-2">Nova Senha</label>
            <input
              type="password"
              name="newPassword"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={newPassword}
              onChange={handleNewPasswordChange}
              required
            />
          </div>
          <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
          >
            Redefinir Senha
          </button>
        </form>
      )}

      <Transition appear show={isOpen} as={React.Fragment}>
        <Dialog
          as="div"
          className="fixed inset-0 z-10 overflow-y-auto"
          onClose={closeModal}
        >
          <div className="min-h-screen px-4 text-center">
            <Transition.Child
              as={React.Fragment}
              enter="ease-out duration-300"
              enterFrom="opacity-0"
              enterTo="opacity-100"
              leave="ease-in duration-300"
              leaveFrom="opacity-100"
              leaveTo="opacity-0"
            >
              <div className="fixed inset-0 bg-black bg-opacity-50" />
            </Transition.Child>

            <span className="inline-block h-screen align-middle" aria-hidden="true">
              ​
            </span>
            <Transition.Child
              as={React.Fragment}
              enter="ease-out duration-300"
              enterFrom="opacity-0 translate-y-4"
              enterTo="opacity-100 translate-y-0"
              leave="ease-in duration-300"
              leaveFrom="opacity-100 translate-y-0"
              leaveTo="opacity-0 translate-y-4"
            >
              <div className="inline-block w-full max-w-md p-6 my-8 overflow-hidden text-left align-middle transition-all transform bg-white shadow-xl rounded-2xl">
                <Dialog.Title as="h3" className="text-lg font-medium leading-6 text-gray-900">
                  Informação
                </Dialog.Title>
                <div className="mt-2">
                  <p className="text-sm text-gray-500">{message ? message.text : ''}</p>
                </div>

                <div className="mt-4">
                  <button
                    type="button"
                    className="inline-flex justify-center px-4 py-2 text-sm font-medium text-white bg-blue-500 border border-transparent rounded-md hover:bg-blue-600 focus:outline-none focus-visible:ring-2 focus-visible:ring-offset-2"
                    onClick={closeModal}
                  >
                    Fechar
                  </button>
                </div>
              </div>
            </Transition.Child>
          </div>
        </Dialog>
      </Transition>
    </div>
  );
}
