import React, { useState } from 'react';
import { Header } from '../../components/Header';
import { Link } from 'react-router-dom';

export function Faqs() {
    // Estado para controlar qual pergunta está expandida
    const [expandedQuestion, setExpandedQuestion] = useState(null);

    // Função para alternar a expansão da pergunta
    const toggleQuestion = (index) => {
        if (expandedQuestion === index) {
            setExpandedQuestion(null); // Fechar se já estiver aberto
        } else {
            setExpandedQuestion(index); // Abrir se estiver fechado
        }
    };

    // Dados das perguntas e respostas
    const faqData = [
        {
            question: 'Como posso me cadastrar?',
            answer: 'Para se cadastrar, basta clicar no link a seguir Registrar-se.',
        },
        {
            question: 'Preciso ter um NIF?',
            answer: 'Sim, para cadastrar na plataforma, precisas ser um empresário ou representante de uma empresa e com NIF válido.',
        },
        {
            question: 'Como funciona a plataforma?',
            answer: 'A plataforma tem como principal objetivo conectar empresas, empreendedores e startups digitais em Angola e outras partes do mundo.',
        },
        {
            question: 'Quais são os tipos de serviços encontrados?',
            answer: 'Os tipos de negócios encontrados são: startups, empreendedores e empresas.',
        },
        {
            question: 'Preciso fazer algum pagamento para registrar-se?',
            answer: 'Não, não precisa ser feito nenhum pagamento para poder registrar-se na plataforma, basta ser apenas um empreendedor ou representante de uma empresa com NIF válido.',
        },
    ];

    return (
        <>
            <Header />
            <section className="bg-gray-900 text-white pt-20 min-h-screen mt-20">
                <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
                    <div className="text-center lg:text-center">
                        <h2 className="text-xl  sm:text-xl md:text-xl font-bold text-white mb-4">Perguntas Frequentes</h2>
                        <p className="text-base sm:text-lg leading-8 mb-8">
                            Não consegue encontrar a resposta que procura?
                            Entre em contato com nossa equipe de{' '}
                            <Link to="" className="text-blue-500 underline">
                                suporte ao cliente
                            </Link>
                            .
                        </p>
                    </div>
                    <div className="grid gap-8">
                        {faqData.map((item, index) => (
                            <div key={index} className="bg-gray-800 rounded-lg shadow-md p-6 cursor-pointer">
                                <div className="flex justify-between items-center" onClick={() => toggleQuestion(index)}>
                                    <h3 className="text-lg sm:text-xl font-medium text-white">{item.question}</h3>
                                    <svg xmlns="http://www.w3.org/2000/svg" className={`h-6 w-6 transform ${expandedQuestion === index ? 'rotate-180' : 'rotate-0'} text-white`} fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" />
                                    </svg>
                                </div>
                                {expandedQuestion === index && (
                                    <p className="text-base sm:text-lg leading-8 mt-2 text-gray-300">{item.answer}</p>
                                )}
                            </div>
                        ))}
                    </div>
                </div>
            </section>
        </>
    );
}
