import React, { useState } from 'react';
import { Dialog } from '@headlessui/react';
import { Header } from '../../components/Header';

import "./Register.module.css";
debugger; 
const areas = [
    "Tecnologia",
    "Contabilidade",
    "Importação e Exportação",
    "Educação",
    "Saúde",
    "Construção Civil",
    "Comércio",
    "Serviços Financeiros",
    "Turismo",
    "Transporte",
    "Alimentação",
    "Artes e Entretenimento",
    "Consultoria",
    "Marketing e Publicidade"
];

const provincias = [
    "Bengo",
    "Benguela",
    "Bié",
    "Cabinda",
    "Cuando Cubango",
    "Cuanza Norte",
    "Cuanza Sul",
    "Cunene",
    "Huambo",
    "Huíla",
    "Luanda",
    "Lunda Norte",
    "Lunda Sul",
    "Malanje",
    "Moxico",
    "Namibe",
    "Uíge",
    "Zaire"
];

export function Register() {
    const [tipo, setTipo] = useState('');
    const [formData, setFormData] = useState({
        email: '',
        endereco: '',
        telefone: '',
        area_atuacao: '',
        provincia: '',
        senha: '',
        nome_representante: '',
        nome_empresa: '',
        nif_empresa: '',
        nome: '',
        genero: '',
        data_nascimento: '',
        bi: ''
    });
    const [errors, setErrors] = useState({});
    const [isSuccessModalOpen, setIsSuccessModalOpen] = useState(false);
    const [isErrorModalOpen, setIsErrorModalOpen] = useState(false);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });

        const validationErrors = validateField(name, value);
        setErrors({ ...errors, [name]: validationErrors });
    };

    const validateField = (fieldName, value) => {
        switch (fieldName) {
            case 'email':
                if (!value || !/\S+@\S+\.\S+/.test(value)) {
                    return 'Por favor, insira um email válido.';
                }
                break;
            case 'telefone':
                if (!value || !/^\d+$/.test(value)) {
                    return 'Por favor, insira um número de telefone válido.';
                }
                break;
            case 'senha':
                if (value.length < 8) {
                    return 'A senha deve ter pelo menos 8 caracteres.';
                }
                break;
            // Adicione validações para outros campos aqui
        }
        return null;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (Object.values(errors).some(error => error)) {
          return;
        }

        try {
          const response = await fetch('../Register', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({ ...formData, tipo })
          });

          if (response.ok) {
            setIsSuccessModalOpen(true);
          } else {
            // Somente converta para JSON se a resposta for ok (status 200-299)
            if (response.status >= 200 && response.status < 300) {
              const data = await response.json();
              setErrors({ ...errors, general: data.error });
              setIsErrorModalOpen(true);
            } else {
              // Se o status não for ok, apenas exiba o modal de erro
              setIsErrorModalOpen(true);
            }
          }
        } catch (error) {
          console.error("Erro durante o envio do formulário:", error);
          setIsErrorModalOpen(true);
        }
      };

    const closeModal = () => {
        setIsSuccessModalOpen(false);
        setIsErrorModalOpen(false);
        setErrors({});
    };

    return (
        <>
            <Header />
            <div className="min-h-screen bg-gray-900 flex flex-col items-center justify-center">
                <form onSubmit={handleSubmit} className="bg-gray-900 p-8 rounded-lg shadow-lg w-full max-w-4xl">
                    <h2 className="text-2xl font-bold text-white mb-3">Registro de Usuário</h2>
                    <p className="text-base  text-gray-400 mb-3">Antes de preencher qualquer outro campo, esolha o teu tipo de usuário</p>
                    <div className="grid grid-cols-3 gap-4">
                        <div className="col-span-1">
                            <div className="mb-4">
                                <label className="block text-gray-400 mb-2">Tipo de Usuário*</label>
                                <select
                                    name="tipo"
                                    value={tipo}
                                    onChange={(e) => setTipo(e.target.value)}
                                    className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                                >
                                    <option value="">Selecione</option>
                                    <option value="empresa">Empresa</option>
                                    <option value="empreendedor">Empreendedor</option>
                                </select>
                            </div>
                        </div>
                        <div className="col-span-2">
                            <div className="mb-4">
                                <label className="block text-gray-400 mb-2">Email*</label>
                                <input
                                    type="email"
                                    name="email"
                                    value={formData.email}
                                    onChange={handleInputChange}
                                    className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent "
                                    required
                                />
                                {errors.email && <span className="text-red-500 text-sm">{errors.email}</span>}
                            </div>
                        </div>
                        <div className="col-span-2">
                            <div className="mb-4">
                                <label className="block text-gray-400 mb-2">Endereço*</label>
                                <input
                                    type="text"
                                    name="endereco"
                                    value={formData.endereco}
                                    onChange={handleInputChange}
                                    className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                                    required
                                />
                            </div>
                        </div>
                        <div className="col-span-1">
                            <div className="mb-4">
                                <label className="block text-gray-400 mb-2">Telefone*</label>
                                <input
                                    type="text"
                                    name="telefone"
                                    value={formData.telefone}
                                    onChange={handleInputChange}
                                    className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                                    required
                                />
                                {errors.telefone && <span className="text-red-500 text-sm">{errors.telefone}</span>}
                            </div>
                        </div>
                        <div className="col-span-1">
                            <div className="mb-4">
                                <label className="block text-gray-400 mb-2">Área de Atuação*</label>
                                <select
                                    name="area_atuacao"
                                    value={formData.area_atuacao}
                                    onChange={handleInputChange}
                                    className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                                    required
                                >
                                    <option value="">Selecione</option>
                                    {areas.map((area, index) => (
                                        <option key={index} value={area}>{area}</option>
                                    ))}
                                </select>
                            </div>
                        </div>
                        <div className="col-span-1">
                            <div className="mb-4">
                                <label className="block text-gray-400 mb-2">Província*</label>
                                <select
                                    name="provincia"
                                    value={formData.provincia}
                                    onChange={handleInputChange}
                                    className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                                    required
                                >
                                    <option value="">Selecione</option>
                                    {provincias.map((provincia, index) => (
                                        <option key={index} value={provincia}>{provincia}</option>
                                    ))}
                                </select>
                            </div>
                        </div>
                        <div className="col-span-1">
                            <div className="mb-4">
                                <label className="block text-gray-400 mb-2">Senha*</label>
                                <input
                                    type="password"
                                    name="senha"
                                    value={formData.senha}
                                    onChange={handleInputChange}
                                    className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                                    required
                                />
                                {errors.senha && <span className="text-red-500 text-sm">{errors.senha}</span>}
                            </div>
                        </div>
                        {tipo === 'empresa' && (
                            <>
                                <div className="col-span-2">
                                    <div className="mb-4">
                                        <label className="block text-gray-400 mb-2">Nome do Representante*</label>
                                        <input
                                            type="text"
                                            name="nome_representante"
                                            value={formData.nome_representante}
                                            onChange={handleInputChange}
                                            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                                            required
                                        />
                                    </div>
                                </div>
                                <div className="col-span-2">
                                    <div className="mb-4">
                                        <label className="block text-gray-400 mb-2">Nome da Empresa*</label>
                                        <input
                                            type="text"
                                            name="nome_empresa"
                                            value={formData.nome_empresa}
                                            onChange={handleInputChange}
                                            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                                            required
                                        />
                                    </div>
                                </div> 

                                <div className="col-span-1">
                                    <div className="mb-4">
                                        <label className="block text-gray-400 mb-2">NIF da Empresa*</label>
                                        <input
                                            type="text"
                                            name="nif_empresa"
                                            value={formData.nif_empresa}
                                            onChange={handleInputChange}
                                            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                                            required
                                        />
                                    </div>
                                </div>
                            </>
                        )}
                        {tipo === 'empreendedor' && (
                            <>
                                <div className="col-span-2">
                                    <div className="mb-4">
                                        <label className="block text-gray-400 mb-2">Nome*</label>
                                        <input
                                            type="text"
                                            name="nome"
                                            value={formData.nome}
                                            onChange={handleInputChange}
                                            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                                            required
                                        />
                                    </div>
                                </div>
                                <div className="col-span-1">
                                    <div className="mb-4">
                                        <label className="block text-gray-400 mb-2">Gênero*</label>
                                        <select
                                            name="genero"
                                            value={formData.genero}
                                            onChange={handleInputChange}
                                            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                                            required
                                        >
                                            <option value="">Selecione</option>
                                            <option value="masculino">Masculino</option>
                                            <option value="feminino">Feminino</option>
                                            <option value="outro">Outro</option>
                                        </select>
                                    </div>
                                </div>
                                <div className="col-span-1">
                                    <div className="mb-4">
                                        <label className="block text-gray-400 mb-2">Data de Nascimento*</label>
                                        <input
                                            type="date"
                                            name="data_nascimento"
                                            value={formData.data_nascimento}
                                            onChange={handleInputChange}
                                            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                                            required
                                        />
                                    </div>
                                </div>
                                <div className="col-span-1">
                                    <div className="mb-4">
                                        <label className="block text-gray-400 mb-2">Nº do BI*</label>
                                        <input
                                            type="text"
                                            name="bi"
                                            value={formData.bi}
                                            onChange={handleInputChange}
                                            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                                            required
                                        />
                                    </div>
                                </div>
                            </>
                        )}
                    </div>
                    <div className="mt-6 flex">
                        <div className='px-4'>
                            <button
                                type="submit"
                                className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                            >
                                Registrar
                            </button>
                        </div>
                        <div>
                            <button
                                type="submit"
                                className="bg-gray-700 hover:bg-gray-800 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                            >
                                Cancelar
                            </button>
                        </div>
                    </div>
                    {errors.general && <span className="text-red-500 text-sm">{errors.general}</span>}
                </form>
            </div>

            {/* Modal de Sucesso */}
            <Dialog
                open={isSuccessModalOpen}
                onClose={closeModal}
                className="fixed z-10 inset-0 overflow-y-auto"
            >
                <div className="flex items-center justify-center min-h-screen">
                    <Dialog.Overlay className="fixed inset-0 bg-black opacity-30" />

                    <div className="bg-white rounded-lg p-6 text-center max-w-md w-full">
                        <Dialog.Title as="h3" className="text-lg font-bold mb-4">Sucesso!</Dialog.Title>
                        <p className="text-gray-700">Seu registro foi concluído com sucesso.</p>
                        <button
                            onClick={closeModal}
                            className="mt-4 bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                        >
                            Fechar
                        </button>
                    </div>
                </div>
            </Dialog>

            {/* Modal de Erro */}
            <Dialog
                open={isErrorModalOpen}
                onClose={closeModal}
                className="fixed z-10 inset-0 overflow-y-auto"
            >
                <div className="flex items-center justify-center min-h-screen">
                    <Dialog.Overlay className="fixed inset-0 bg-black opacity-30" />

                    <div className="bg-white rounded-lg p-6 text-center max-w-md w-full">
                        <Dialog.Title as="h3" className="text-lg font-bold mb-4">Erro!</Dialog.Title>
                        <p className="text-gray-700">Ocorreu um erro ao processar seu registro. Por favor, tente novamente mais tarde.</p>
                        <button
                            onClick={closeModal}
                            className="mt-4 bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                        >
                            Fechar
                        </button>
                    </div>
                </div>
            </Dialog>
        </>
    );
}
