import React, { useState } from 'react';
import axios from 'axios';

const UpdateProfile = ({ userType, userId }) => {
  const [formData, setFormData] = useState({
    email: '',
    telefone: '',
    provincia: '',
    area_atuacao: '',
    // Campos adicionais para empresa
    nif_empresa: '',
    nome_empresa: '',
    nome_representante: '',
    ano_existencia: '',
    // Campos adicionais para empreendedor
    bi: '',
    nome: '',
    data_nascimento: '',
    genero: ''
  });

  const [modalMessage, setModalMessage] = useState('');
  const [modalVisible, setModalVisible] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const endpoint = userType === 'empresa' ? '/update/profile/empresa' : '/update/profile/empreendedor';
    axios.put(endpoint, { ...formData, userId })
      .then(response => {
        setModalMessage(response.data.message);
        setModalVisible(true);
      })
      .catch(error => {
        setModalMessage('Erro ao atualizar perfil. Tente novamente.');
        setModalVisible(true);
      });
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded-lg shadow-md w-3/4 md:w-1/2 lg:w-1/3">
        <h2 className="text-2xl font-bold mb-6 text-center">Atualizar Perfil de {userType === 'empresa' ? 'Empresa' : 'Empreendedor'}</h2>

        {/* Campos Comuns */}
        <div className="mb-4">
          <label className="block text-gray-700">Email:</label>
          <input type="email" name="email" value={formData.email} onChange={handleChange} className="w-full px-3 py-2 border-b-2 border-gray-300 focus:outline-none" />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Telefone:</label>
          <input type="text" name="telefone" value={formData.telefone} onChange={handleChange} className="w-full px-3 py-2 border-b-2 border-gray-300 focus:outline-none" />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Província:</label>
          <input type="text" name="provincia" value={formData.provincia} onChange={handleChange} className="w-full px-3 py-2 border-b-2 border-gray-300 focus:outline-none" />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Área de Atuação:</label>
          <input type="text" name="area_atuacao" value={formData.area_atuacao} onChange={handleChange} className="w-full px-3 py-2 border-b-2 border-gray-300 focus:outline-none" />
        </div>

        {/* Campos Específicos */}
        {userType === 'empresa' ? (
          <>
            <div className="mb-4">
              <label className="block text-gray-700">NIF da Empresa:</label>
              <input type="text" name="nif_empresa" value={formData.nif_empresa} onChange={handleChange} className="w-full px-3 py-2 border-b-2 border-gray-300 focus:outline-none" />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700">Nome da Empresa:</label>
              <input type="text" name="nome_empresa" value={formData.nome_empresa} onChange={handleChange} className="w-full px-3 py-2 border-b-2 border-gray-300 focus:outline-none" />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700">Nome do Representante:</label>
              <input type="text" name="nome_representante" value={formData.nome_representante} onChange={handleChange} className="w-full px-3 py-2 border-b-2 border-gray-300 focus:outline-none" />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700">Ano de Existência:</label>
              <input type="text" name="ano_existencia" value={formData.ano_existencia} onChange={handleChange} className="w-full px-3 py-2 border-b-2 border-gray-300 focus:outline-none" />
            </div>
          </>
        ) : (
          <>
            <div className="mb-4">
              <label className="block text-gray-700">BI:</label>
              <input type="text" name="bi" value={formData.bi} onChange={handleChange} className="w-full px-3 py-2 border-b-2 border-gray-300 focus:outline-none" />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700">Nome:</label>
              <input type="text" name="nome" value={formData.nome} onChange={handleChange} className="w-full px-3 py-2 border-b-2 border-gray-300 focus:outline-none" />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700">Data de Nascimento:</label>
              <input type="date" name="data_nascimento" value={formData.data_nascimento} onChange={handleChange} className="w-full px-3 py-2 border-b-2 border-gray-300 focus:outline-none" />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700">Gênero:</label>
              <input type="text" name="genero" value={formData.genero} onChange={handleChange} className="w-full px-3 py-2 border-b-2 border-gray-300 focus:outline-none" />
            </div>
          </>
        )}

        <button type="submit" className="w-full bg-blue-500 text-white py-2 rounded-md hover:bg-blue-600 focus:outline-none">Atualizar Perfil</button>

        {/* Modal de Confirmação ou Erro */}
        {modalVisible && (
          <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
            <div className="bg-white p-6 rounded-lg shadow-lg">
              <p>{modalMessage}</p>
              <button onClick={() => setModalVisible(false)} className="mt-4 bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 focus:outline-none">Fechar</button>
            </div>
          </div>
        )}
      </form>
    </div>
  );
};

export default UpdateProfile;
