import React, { useState } from "react";
import CarexLogo from "../../assets/img/carex_logo.png";

export function CreatePost() {
  const [postContent, setPostContent] = useState("");

  const handlePost = () => {
    // Aqui você pode enviar o post para algum backend ou apenas atualizar o estado local
    console.log("Novo post:", postContent);
    setPostContent("");
  };

  return (
    <div className="create-post-container bg-gray-800 p-4 rounded-md mb-4">
      <div className="flex items-center mb-4">
        <img src={CarexLogo} alt="User" className="w-14 h-14 rounded-full mr-4" />
        <input
          type="text"
          placeholder="Crie uma publicação..."
          value={postContent}
          onChange={(e) => setPostContent(e.target.value)}
          className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
        />
      </div>
      <button onClick={handlePost} className="bg-blue-500 hover:bg-blue-600 text-white text-sm py-2 px-4 rounded-full">
        Postar
      </button>
    </div>
  );
}
