import React, { useState, useEffect } from 'react';
import { Header } from '../../components/Header';
import areas from '../../JSON/areas.json';
import provincias from '../../JSON/provincias.json';
import './Register.module.css'; // Import the CSS file for custom styling

export const Register = () => {
  const [tipoUsuario, setTipoUsuario] = useState('empresa');
  const [formData, setFormData] = useState({
    email: '',
    endereco: '',
    telefone: '',
    area_atuacao: '',
    provincia: '',
    senha: '',
    nome_representante: '',
    nome_empresa: '',
    nif_empresa: '',
    nome: '',
    genero: '',
    data_nascimento: '',
    bi: ''
  });
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [showErrorModal, setShowErrorModal] = useState(false);

  useEffect(() => {
    setFormData({
      email: '',
      endereco: '',
      telefone: '',
      area_atuacao: '',
      provincia: '',
      senha: '',
      nome_representante: '',
      nome_empresa: '',
      nif_empresa: '',
      nome: '',
      genero: '',
      data_nascimento: '',
      bi: ''
    });
  }, [tipoUsuario]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Simulate API call to save data in MySQL database
    try {
      // Assume registration is successful
      setShowSuccessModal(true);
    } catch (error) {
      setShowErrorModal(true);
    }
  };

  return (
    <>
      <Header />
      <div className="bg-gray-900 min-h-screen flex items-center justify-center">
        <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-lg">
          <h2 className="text-2xl font-bold text-gray-800 mb-6">Registro de Usuário</h2>
          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Tipo de Usuário</label>
              <select
                className="w-full p-2 border border-gray-300 rounded"
                value={tipoUsuario}
                onChange={(e) => setTipoUsuario(e.target.value)}
              >
                <option value="empresa">Empresa</option>
                <option value="empreendedor">Empreendedor</option>
              </select>
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Email</label>
              <input
                type="email"
                name="email"
                className="w-full p-2 border border-gray-300 rounded"
                value={formData.email}
                onChange={handleChange}
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Endereço</label>
              <input
                type="text"
                name="endereco"
                className="w-full p-2 border border-gray-300 rounded"
                value={formData.endereco}
                onChange={handleChange}
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Telefone</label>
              <input
                type="text"
                name="telefone"
                className="w-full p-2 border border-gray-300 rounded"
                value={formData.telefone}
                onChange={handleChange}
              />
            </div>
            <div>
                <label htmlFor="actuacao" className="block text-sm font-medium leading-6 text-white">Área de Atuação</label>
                <select type="text" name="actuacao" id="actuacao" value={actuacao} onChange={(e) => setActuacao(e.target.value)} required autoComplete="off" className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm placeholder-gray-400 focus:ring-2 focus:ring-indigo-500 sm:text-sm sm:leading-6">
                  <option value=""></option>
                  {areas.areasAtuacao.map((area, index) => (
                    <option key={index} value={area}>{area}</option>
                  ))}
                </select>
              </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Província</label>
              <select
                name="provincia"
                className="w-full p-2 border border-gray-300 rounded"
                value={formData.provincia}
                onChange={handleChange}
              >
                {provincias.map((provincia, index) => (
                  <option key={index} value={provincia}>
                    {provincia}
                  </option>
                ))}
              </select>
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2">Senha</label>
              <input
                type="password"
                name="senha"
                className="w-full p-2 border border-gray-300 rounded"
                value={formData.senha}
                onChange={handleChange}
              />
            </div>
            {tipoUsuario === 'empresa' && (
              <>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Nome do Representante</label>
                  <input
                    type="text"
                    name="nome_representante"
                    className="w-full p-2 border border-gray-300 rounded"
                    value={formData.nome_representante}
                    onChange={handleChange}
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Nome da Empresa</label>
                  <input
                    type="text"
                    name="nome_empresa"
                    className="w-full p-2 border border-gray-300 rounded"
                    value={formData.nome_empresa}
                    onChange={handleChange}
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">NIF da Empresa</label>
                  <input
                    type="text"
                    name="nif_empresa"
                    className="w-full p-2 border border-gray-300 rounded"
                    value={formData.nif_empresa}
                    onChange={handleChange}
                  />
                </div>
              </>
            )}
            {tipoUsuario === 'empreendedor' && (
              <>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Nome</label>
                  <input
                    type="text"
                    name="nome"
                    className="w-full p-2 border border-gray-300 rounded"
                    value={formData.nome}
                    onChange={handleChange}
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Gênero</label>
                  <select
                    name="genero"
                    className="w-full p-2 border border-gray-300 rounded"
                    value={formData.genero}
                    onChange={handleChange}
                  >
                    <option value="masculino">Masculino</option>
                    <option value="feminino">Feminino</option>
                  </select>
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Data de Nascimento</label>
                  <input
                    type="date"
                    name="data_nascimento"
                    className="w-full p-2 border border-gray-300 rounded"
                    value={formData.data_nascimento}
                    onChange={handleChange}
                  />
                </div>
                <div className="mb-4">
                  <label className="block text-gray-700 mb-2">Número de Identificação (BI)</label>
                  <input
                    type="text"
                    name="bi"
                    className="w-full p-2 border border-gray-300 rounded"
                    value={formData.bi}
                    onChange={handleChange}
                  />
                </div>
              </>
            )}
            <button
              type="submit"
              className="w-full bg-blue-500 text-white p-2 rounded"
            >
              Registrar
            </button>
          </form>
        </div>
      </div>

      {showSuccessModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded shadow-lg">
            <h2 className="text-2xl font-bold mb-4">Registro bem-sucedido!</h2>
            <p className="mb-4">Seu registro foi concluído com sucesso.</p>
            <button
              onClick={() => setShowSuccessModal(false)}
              className="bg-green-500 text-white px-4 py-2 rounded"
            >
              OK
            </button>
          </div>
        </div>
      )}

      {showErrorModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded shadow-lg">
            <h2 className="text-2xl font-bold mb-4">Erro no Registro</h2>
            <p className="mb-4">Ocorreu um erro ao tentar registrar. Por favor, tente novamente.</p>
            <button
              onClick={() => setShowErrorModal(false)}
              className="bg-red-500 text-white px-4 py-2 rounded"
            >
              OK
            </button>
          </div>
        </div>
      )}
    </>
  );
};
