import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Dialog, Transition } from '@headlessui/react';
import { Link } from 'react-router-dom';

const areas = ['Saude', 'Financas', 'Educacao', 'Marketing', 'Tecnologia', 'Exportacao e Importacao', 'Agricultura', 'Beleza', 'Outro'];
const provincias = ['Bengo', 'Benguela', 'Bie', 'Cabinda', 'Cuanza Norte', 'Cuanza Sul', 'Cuando Cubango', 'Cunene', 'Huambo', 'Huíla', 'Luanda', 'Lunda Norte', 'Lunda Sul', 'Malanje', 'Moxico', 'Namibe', 'Uíge', 'Zaire'];

export const ProfileEdit = ({ userType, userId }) => {
  const [formData, setFormData] = useState({
    email: '',
    telefone: '',
    provincia: '',
    area_atuacao: '',
    // Campos adicionais para empresa
    nif_empresa: '',
    nome_empresa: '',
    nome_representante: '',
    ano_existencia: '',
    // Campos adicionais para empreendedor
    bi: '',
    nome: '',
    data_nascimento: '',
    genero: ''
  });

  const [modalMessage, setModalMessage] = useState('');
  const [modalVisible, setModalVisible] = useState(false);

  useEffect(() => {
    // Puxar dados do usuário para preencher o formulário
    const fetchData = async () => {
      try {
        const response = await axios.get(`/profile/${userType}/${userId}`);
        setFormData(response.data);
      } catch (error) {
        console.error('Erro ao buscar dados do perfil', error);
      }
    };
    fetchData();
  }, [userType, userId]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const endpoint = userType === 'empresa' ? '/update/profile/empresa' : '/update/profile/empreendedor';
    axios.put(endpoint, { ...formData, userId })
      .then(response => {
        setModalMessage(response.data.message);
        setModalVisible(true);
      })
      .catch(error => {
        setModalMessage('Erro ao atualizar perfil. Tente novamente.');
        setModalVisible(true);
      });
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  return (
    <div className="bg-gray-900 p-8 rounded-lg shadow-lg w-full max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold text-white mb-3">Editar Perfil de {userType === 'empresa' ? 'Empresa' : 'Empreendedor'}</h2>
      <form onSubmit={handleSubmit} className='grid-cols-1'>
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
          <div className='col-span-2'>
            <label className="block text-gray-400 mb-2">Nome</label>
            <input
              type="text"
              name="nome"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={formData.nome}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label className="block text-gray-400 mb-2">Email</label>
            <input
              type="email"
              name="email"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label className="block text-gray-400 mb-2">Telefone</label>
            <input
              type="text"
              name="telefone"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={formData.telefone}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label className="block text-gray-400 mb-2">Província</label>
            <select
              name="provincia"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={formData.provincia}
              onChange={handleChange}
              required
            >
              <option value="">Selecione</option>
              {provincias.map((provincia) => (
                <option key={provincia} value={provincia}>{provincia}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-gray-400 mb-2">Área de Atuação</label>
            <select
              name="area_atuacao"
              className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
              value={formData.area_atuacao}
              onChange={handleChange}
              required
            >
              <option value="">Selecione</option>
              {areas.map((area) => (
                <option key={area} value={area}>{area}</option>
              ))}
            </select>
          </div>

          {userType === 'empresa' ? (
            <>
              <div className="mb-4">
                <label className="block text-gray-400 mb-2">NIF da Empresa:</label>
                <input
                  type="text"
                  name="nif_empresa"
                  className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                  value={formData.nif_empresa}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-400 mb-2">Nome da Empresa:</label>
                <input
                  type="text"
                  name="nome_empresa"
                  className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                  value={formData.nome_empresa}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-400 mb-2">Nome do Representante:</label>
                <input
                  type="text"
                  name="nome_representante"
                  className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                  value={formData.nome_representante}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="mb-4">
                <label className="block text-gray-400 mb-2">Ano de Existência:</label>
                <input
                  type="text"
                  name="ano_existencia"
                  className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                  value={formData.ano_existencia}
                  onChange={handleChange}
                  required
                />
              </div>
            </>
          ) : (
            <>
              <div>
                <label className="block text-gray-400 mb-2">BI</label>
                <input
                  type="text"
                  name="bi"
                  className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                  value={formData.bi}
                  onChange={handleChange}
                  required
                />
              </div>
              <div>
                <label className="block text-gray-400 mb-2">Data de Nascimento</label>
                <input
                  type="date"
                  name="data_nascimento"
                  className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                  value={formData.data_nascimento}
                  onChange={handleChange}
                  required
                />
              </div>
              <div>
                <label className="block text-gray-400 mb-2">Gênero</label>
                <select
                  name="genero"
                  className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
                  value={formData.genero}
                  onChange={handleChange}
                  required
                >
                  <option value="">Selecione</option>
                  <option value="Masculino">Masculino</option>
                  <option value="Feminino">Feminino</option>
                  <option value="Outro">Outro</option>
                </select>
              </div>
            </>
          )}
        </div>

        <div className='flex justify-end'>
          <div className='mr-3'>
            <button className="col-span-1 mt-4 bg-gray-700 hover:bg-gray-800 text-white font-bold py-2 px-4 rounded">
              <Link to="/">Cancelar</Link>
            </button>
          </div>
          <div>
            <button type="submit" className="col-span-1 mt-4 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
              Atualizar Perfil
            </button>
          </div>
        </div>
      </form>
     {/* Modal de feedback após a atualização */}
     <Transition appear show={modalVisible} as={React.Fragment}>
        <Dialog
          as="div"
          className="fixed inset-0 z-10 overflow-y-auto"
          onClose={closeModal}
        >
          <div className="min-h-screen px-4 text-center">
            <Transition.Child
              as={React.Fragment}
              enter="ease-out duration-900"
              enterFrom="opacity-0"
              enterTo="opacity-100"
              leave="ease-in duration-900"
              leaveFrom="opacity-100"
              leaveTo="opacity-0"
            >
              <div className="fixed inset-0 bg-black bg-opacity-50" />
            </Transition.Child>

            <span className="inline-block h-screen align-middle" aria-hidden="true">
              ​
            </span>
            <Transition.Child
              as={React.Fragment}
              enter="ease-out duration-900"
              enterFrom="opacity-0 translate-y-4"
              enterTo="opacity-100 translate-y-0"
              leave="ease-in duration-900"
              leaveFrom="opacity-100 translate-y-0"
              leaveTo="opacity-0 translate-y-4"
            >
              <div className="inline-block w-full max-w-md p-6 my-8 overflow-hidden text-left align-middle transition-all transform bg-white shadow-xl rounded-2xl">
                <Dialog.Title as="h3" className="text-lg font-medium leading-6 text-gray-900">Informação</Dialog.Title>
                <div className="mt-2">
                  <p className="text-sm text-gray-500">{modalMessage}</p>
                </div>

                <div className="mt-4">
                  <button
                    type="button"
                    className="inline-flex justify-center px-4 py-2 text-sm font-medium text-white bg-blue-500 border border-transparent rounded-md hover:bg-blue-600 focus:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:ring-blue-500"
                    onClick={closeModal}
                  >
                    Fechar
                  </button>
                </div>
              </div>
            </Transition.Child>
          </div>
        </Dialog>
      </Transition>
    </div>
  );
};
