import React, { useState } from 'react';
import { FaUserTie, FaUserGraduate } from 'react-icons/fa';
import axios from 'axios';

// Importe os componentes dos formulários
import {FormularioEmpresa} from './FormularioEmpresa';
import {FormularioEmpreendedor} from './FormularioEmpreendedor';



export function Register() {
  const [tipoUsuario, setTipoUsuario] = useState('');
  const [isModalSucessoAberto, setIsModalSucessoAberto] = useState(false);
  const [isModalErroAberto, setIsModalErroAberto] = useState(false);

  const handleTipoUsuarioChange = (event) => {
    setTipoUsuario(event.target.value);
  };

  const handleSucesso = () => {
    setIsModalSucessoAberto(true);
  };

  const handleErro = () => {
    setIsModalErroAberto(true);
  };

  const fecharModalSucesso = () => {
    setIsModalSucessoAberto(false);
  };

  const fecharModalErro = () => {
    setIsModalErroAberto(false);
  };

  return (
    <div className="container">
      <h1 className="text-center text-3xl font-bold mb-4">Registro</h1>

      <div className="flex justify-center mb-4">
        <select value={tipoUsuario} onChange={handleTipoUsuarioChange} className="bg-gray-200 appearance-none border border-gray-300 rounded-md py-2 px-4 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500">
          <option value="">Selecione o tipo de usuário</option>
          <option value="empresa">Empresa</option>
          <option value="empreendedor">Empreendedor</option>
        </select>
      </div>

      {/* Renderiza o formulário de acordo com o tipo de usuário selecionado */}
      {tipoUsuario === 'empresa' && (
        <FormularioEmpresa onSucesso={handleSucesso} onErro={handleErro} />
      )}
      {tipoUsuario === 'empreendedor' && (
        <FormularioEmpreendedor onSucesso={handleSucesso} onErro={handleErro} />
      )}

      {/* Modais (Agora dentro do componente Registro) */}
      <ModalSucesso isOpen={isModalSucessoAberto} onClose={fecharModalSucesso} />
      <ModalErro isOpen={isModalErroAberto} onClose={fecharModalErro} />

    </div>
  );
}
