import React, { useState } from "react";
import CarexLogo from "../../assets/img/carex_logo.png";
import { FaThumbsUp, FaShare, FaChevronCircleRight } from 'react-icons/fa';

export function MainPost({ post, onLike, onComment, onDelete }) {
  const { id, text, image, likes, isLiked, comments, author } = post;
  const lastComment = comments.length > 0 ? comments[comments.length - 1] : null;

  return (
    <div className="main-post bg-gray-800 text-white p-4 rounded mb-4">
      <div className="post-header flex items-center mb-4">
        <img src={author.photo} alt={author.name} className="w-10 h-10 rounded-full mr-4" />
        <div>
          <h2 className="font-bold">{author.name}</h2>
        </div>
      </div>
      <div className="post-body mb-4">
        <p>{text}</p>
        {image && <img src={image} alt="" className="mt-4" />}
      </div>
      <div className="post-actions flex justify-between items-center">
        <button 
          className={`like-button ${isLiked ? 'text-blue-500' : 'text-gray-400'}`}
          onClick={() => onLike(id)}
        >
          Like ({likes})
        </button>
        <button 
          className="comment-button text-gray-400"
          onClick={() => onComment(id, prompt('Digite seu comentário'))}
        >
          Comment
        </button>
        <button 
          className="delete-button text-red-500"
          onClick={() => onDelete(id)}
        >
          Delete
        </button>
      </div>
      {lastComment && (
        <div className="last-comment mt-4">
          <p className="text-sm text-gray-400">Último comentário por {lastComment.author}: {lastComment.text}</p>
        </div>
      )}
    </div>
  );
}
