import React, { useState } from 'react';
import axios from 'axios';
import { Dialog, Transition } from '@headlessui/react';
import { ExclamationIcon } from '@heroicons/react/24/solid';

const areas = ['Saude', 'Financas', 'Educacao', 'Marketing', 'Tecnologia', 'Exportacao e Importacao', 'Agricultura', 'Beleza', 'Outro'];
const provincias = ['Bengo', 'Benguela', 'Bie', 'Cabinda', 'Cuanza Norte', 'Cuanza Sul', 'Cuando Cubango', 'Cunene', 'Huambo', 'Huíla', 'Luanda', 'Lunda Norte', 'Lunda Sul', 'Malanje', 'Moxico', 'Namibe', 'Uíge', 'Zaire'];

export function FormularioEmpresa() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    telefone: '',
    provincia: '',
    area_atuacao: '',
    nif_empresa: '',
    nome_empresa: '',
    nome_representante: '',
    ano_existencia: ''
  });

  const [message, setMessage] = useState(null);
  const [isOpen, setIsOpen] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/register/empresa', formData);
      setMessage({ type: 'success', text: response.data.message });
      setIsOpen(true);
    } catch (error) {
      setMessage({ type: 'error', text: 'Erro ao registrar empresa' });
      setIsOpen(true);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="bg-gray-900 p-8 rounded-lg shadow-lg w-full max-w-md mx-auto">
      <h2 className="text-2xl font-bold text-white mb-3">Registro de Empresa</h2>
      <div className="grid grid-cols-1 gap-4">
        <div>
          <label className="block text-gray-400 mb-2">Email</label>
          <input
            type="email"
            name="email"
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label className="block text-gray-400 mb-2">Senha</label>
          <input
            type="password"
            name="password"
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label className="block text-gray-400 mb-2">Telefone</label>
          <input
            type="text"
            name="telefone"
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            value={formData.telefone}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label className="block text-gray-400 mb-2">Província</label>
          <select
            name="provincia"
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            value={formData.provincia}
            onChange={handleChange}
            required
          >
            <option value="">Selecione</option>
            {provincias.map((provincia) => (
              <option key={provincia} value={provincia}>{provincia}</option>
            ))}
          </select>
        </div>
        <div>
          <label className="block text-gray-400 mb-2">Área de Atuação</label>
          <select
            name="area_atuacao"
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            value={formData.area_atuacao}
            onChange={handleChange}
            required
          >
            <option value="">Selecione</option>
            {areas.map((area) => (
              <option key={area} value={area}>{area}</option>
            ))}
          </select>
        </div>
        <div>
          <label className="block text-gray-400 mb-2">NIF</label>
          <input
            type="text"
            name="nif_empresa"
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            value={formData.nif_empresa}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label className="block text-gray-400 mb-2">Nome da Empresa</label>
          <input
            type="text"
            name="nome_empresa"
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            value={formData.nome_empresa}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label className="block text-gray-400 mb-2">Nome do Representante</label>
          <input
            type="text"
            name="nome_representante"
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            value={formData.nome_representante}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label className="block text-gray-400 mb-2">Ano de Existência</label>
          <input
            type="number"
            name="ano_existencia"
            className="w-full p-2 rounded bg-gray-700 text-white focus:ring-transparent"
            value={formData.ano_existencia}
            onChange={handleChange}
            required
          />
        </div>
      </div>
      <div className="mt-4">
        <button type="submit" className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded w-full">
          Registrar
        </button>
      </div>

      <Transition appear show={isOpen} as={React.Fragment}>
        <Dialog
          as="div"
          className="fixed inset-0 z-10 overflow-y-auto"
          onClose={() => setIsOpen(false)}
        >
          <div className="min-h-screen px-4 text-center">
            <Transition.Child
              as={React.Fragment}
              enter="ease-out duration-300"
              enterFrom="opacity-0"
              enterTo="opacity-100"
              leave="ease-in duration-200"
              leaveFrom="opacity-100"
              leaveTo="opacity-0"
            >
              <Dialog.Overlay className="fixed inset-0 bg-black bg-opacity-50" />
            </Transition.Child>

            <span className="inline-block h-screen align-middle" aria-hidden="true">
              &#8203;
            </span>

            <Transition.Child
              as={React.Fragment}
              enter="ease-out duration-300"
              enterFrom="opacity-0 scale-95"
              enterTo="opacity-100 scale-100"
              leave="ease-in duration-200"
              leaveFrom="opacity-100 scale-100"
              leaveTo="opacity-0 scale-95"
            >
              <div className="inline-block w-full max-w-md p-6 my-8 overflow-hidden text-left align-middle transition-all transform bg-white shadow-xl rounded-lg">
                <Dialog.Title as="h3" className="text-lg font-medium leading-6 text-gray-900">
                  {message && (message.type === 'success' ? 'Sucesso' : 'Erro')}
                </Dialog.Title>
                <div className="mt-2">
                  <p className="text-sm text-gray-500">
                    {message && message.text}
                  </p>
                </div>
                <div className="mt-4">
                  <button
                    type="button"
                    className="inline-flex justify-center px-4 py-2 text-sm font-medium text-gray-900 bg-gray-100 border border-transparent rounded-md hover:bg-gray-200 focus:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 focus-visible:ring-blue-500"
                    onClick={() => setIsOpen(false)}
                  >
                    Fechar
                  </button>
                </div>
              </div>
            </Transition.Child>
          </div>
        </Dialog>
      </Transition>
    </form>
  );
}
