import React, { useState } from "react";
import CarexLogo from "../../assets/img/carex_logo.png";
import { FaCamera, FaVideo, FaLine, FaCalendar } from 'react-icons/fa6';

export function CreatePost() {
  const [postType, setPostType] = useState('normal'); // Estado para controlar o tipo de post
  const [mediaFile, setMediaFile] = useState(null); // Estado para armazenar arquivo de mídia selecionado

  const handlePostTypeChange = (type) => {
    setPostType(type);
  };

  const handleMediaChange = (e) => {
    const file = e.target.files[0];
    setMediaFile(file);
  };

  const renderPostForm = () => {
    switch (postType) {
      case 'normal':
        return (
          <input
            type="text"
            placeholder="Cria uma publicação"
            className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white"
          />
        );
      case 'meet':
        return (
          <>
            <input
              type="text"
              placeholder="Título do Meet"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <input
              type="text"
              placeholder="Link da sala"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
            </div>
          </>
        );
      case 'startup':
        return (
          <>
            <input
              type="text"
              placeholder="Nome da Startup"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <textarea
              placeholder="Descrição da Startup"
              className="w-full h-20 focus-visible:ring-transparent rounded-md pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
            </div>
          </>
        );
      case 'evento':
        return (
          <>
            <input
              type="text"
              placeholder="Nome do Evento"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <input
              type="text"
              placeholder="Data e hora"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <input
              type="text"
              placeholder="Local do Evento"
              className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
            />
            <div>
              <label className="block text-white font-semibold mb-1">Upload de Imagem/Vídeo:</label>
              <input
                type="file"
                accept="image/*, video/*"
                onChange={handleMediaChange}
                className="w-full h-10 focus-visible:ring-transparent rounded-full pl-4 border border-gray-700 bg-gray-700 text-white mb-2"
              />
            </div>
          </>
        );
      default:
        return null;
    }
  };

  return (
    <div className="bg-gray-900 border border-gray-700 rounded-md p-4 mb-4">
      <div className="second-col-section-home flex items-center mb-4 bg-gray-900">
        <img src={CarexLogo} alt="" className="w-14 h-14 rounded-full mr-4"/>
        {renderPostForm()}
      </div>
      <div className="page-home-uploads-section justify-between">
        <ul className="flex justify-between text-white font-semibold">
          <li className={`flex items-center mr-1 cursor-pointer ${postType === 'normal' ? 'text-blue-500' : 'text-white'}`} onClick={() => handlePostTypeChange('normal')}>
            <FaCamera className="mr-1" /> Mídias
          </li>
          <li className={`flex items-center px-2 cursor-pointer ${postType === 'meet' ? 'text-blue-500' : 'text-white'}`} onClick={() => handlePostTypeChange('meet')}>
            <FaVideo className="mr-1" /> Meet
          </li>
          <li className={`flex items-center px-2 cursor-pointer ${postType === 'startup' ? 'text-blue-500' : 'text-white'}`} onClick={() => handlePostTypeChange('startup')}>
            <FaLine className="mr-1" /> StartUp
          </li>
          <li className={`flex items-center cursor-pointer ${postType === 'evento' ? 'text-blue-500' : 'text-white'}`} onClick={() => handlePostTypeChange('evento')}>
            <FaCalendar className="mr-1" /> Evento
          </li>
        </ul>
      </div>
    </div>
  );
}
