import React, { useState } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import axios from 'axios';

// Dados para o formulário
const areas = ['Saude', 'Financas', 'Educacao', 'Marketing', 'Tecnologia', 'Exportacao e Importacao', 'Agricultura', 'Beleza', 'Outro'];
const provincias = ['Bengo', 'Benguela', 'Bie', 'Cabinda', 'Cuanza Norte', 'Cuanza Sul', 'Cuando Cubango', 'Cunene', 'Huambo', 'Huíla', 'Luanda', 'Lunda Norte', 'Lunda Sul', 'Malanje', 'Moxico', 'Namibe', 'Uíge', 'Zaire', 'Kwanza Norte', 'Kwanza Sul', 'Kwando Kubango', 'Moçâmedes', 'Luanda', 'Malanje'];

export function FormularioEmpresa() {
  const [showSuccess, setShowSuccess] = useState(false);
  const [showError, setShowError] = useState(false);

  // Função para lidar com o envio do formulário
  const handleRegister = async (values) => {
    try {
      const response = await axios.post("http://localhost:3001/register/empresa", {
        email: values.email,
        password: values.password,
        nome_representante: values.nome_representante,
        nome_empresa: values.nome_empresa,
        nif_empresa: values.nif_empresa,
        area_atuacao: values.area_atuacao,
        provincia: values.provincia,
        telefone: values.telefone,
        endereco: values.endereco,
      });

      if (response.status === 201) {
        setShowSuccess(true);
        setShowError(false);
      } else {
        setShowError(true);
        setShowSuccess(false);
      }
    } catch (error) {
      setShowError(true);
      setShowSuccess(false);
    }
  };

  // Função para lidar com o cancelamento do registro
  const handleCancel = () => {
    setShowSuccess(false);
    setShowError(false);
  };

  // Validação do formulário
  const validateRegister = Yup.object().shape({
    email: Yup.string().email('Endereço de email inválido').required('Por favor, insira o seu endereço de email'),
    password: Yup.string().min(8).required('Por favor, insira a sua palavra-passe'),
    nome_representante: Yup.string().required('Por favor, insira o nome do seu representante'),
    nome_empresa: Yup.string().required('Por favor, insira o nome da sua empresa'),
    nif_empresa: Yup.string().required('Por favor, insira o NIF da sua empresa'),
    area_atuacao: Yup.string().required('Por favor, escolha a área de atuação'),
    provincia: Yup.string().required('Por favor, escolha a província'),
    telefone: Yup.string().required('Por favor, insira o seu telefone'),
    endereco: Yup.string().required('Por favor, insira o seu endereço'),
  });

  return (
    <Formik
      onSubmit={handleRegister}
      initialValues={{}}
      validationSchema={validateRegister}
    >
      <Form className="bg-gray-900 p-8 rounded-lg shadow-lg w-full max-w-4xl">
        <h2 className="text-2xl font-bold text-white mb-3">Registro de Empresa</h2>
        <div className="grid grid-cols-3 gap-4">
          {/* Campos do formulário aqui */}
        </div>

        <div className="mt-6 flex">
          <div className='px-4'>
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Registrar
            </button>
          </div>
          <div>
            <button
              type="button"
              onClick={handleCancel}
              className="bg-gray-700 hover:bg-gray-800 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              Cancelar
            </button>
          </div>
        </div>

        {showSuccess && (
          <div className="mt-4 text-green-500">
            Usuário registrado com sucesso!
          </div>
        )}

        {showError && (
          <div className="mt-4 text-red-500">
            Ocorreu um erro durante o registro. Por favor, tente novamente.
          </div>
        )}
      </Form>
    </Formik>
  );
}
