import React, { useState } from 'react';
import { FaSuitcase, FaSave, FaArrowLeft, FaCamera, FaImages } from 'react-icons/fa';
import { FaLocationPin, FaHouse } from 'react-icons/fa6';
import { Link } from 'react-router-dom';

export function EditProfile() {
  const [businessType, setBusinessType] = useState('Importação e exportação de produtos diversos');
  const [address, setAddress] = useState('Edifício Azul AP. 101 - 3º Andar');
  const [location, setLocation] = useState('Luanda, Angola - Largo da Mutamba');
  const [connections, setConnections] = useState(97);
  const [profileImage, setProfileImage] = useState(null);
  const [coverImage, setCoverImage] = useState(null);

  const saveChanges = () => {
    console.log('Alterações salvas!');
  };

  const handleProfileImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfileImage(URL.createObjectURL(file));
    }
  };

  const handleCoverImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setCoverImage(URL.createObjectURL(file));
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen py-6 bg-gray-900">
      <div className="max-w-4xl w-full bg-gray.900 shadow-md rounded-lg overflow-hidden">
        <div className="bg-blue-600 px-4 py-6">
          <div className="flex items-center justify-between">
            <h1 className="text-2xl font-bold text-white">Editar Perfil</h1>
            <Link to="/" className="text-white hover:underline">
              <FaArrowLeft className="inline-block mr-1" /> Voltar
            </Link>
          </div>
        </div>

        <div className="p-6">
          <div className="space-y-4">
            {/* Capa */}
            <div className="relative">
              <label htmlFor="coverImage" className="cursor-pointer">
                {coverImage ? (
                  <img src={coverImage} alt="Cover" className="w-full h-48 object-cover" />
                ) : (
                  <div className="w-full h-48 bg-gray-300 flex items-center justify-center text-gray-500">
                    <FaImages size={48} />
                    <p className="mt-2">Clique para adicionar uma capa</p>
                  </div>
                )}
                <input
                  type="file"
                  id="coverImage"
                  accept="image/*"
                  onChange={handleCoverImageChange}
                  className="hidden"
                />
              </label>
              <div className="absolute bottom-2 right-2 text-white">
                <FaCamera size={24} />
              </div>
            </div>

            {/* Foto de Perfil */}
            <div className="relative -mt-16">
              <div className="flex justify-center items-center">
                <label htmlFor="profileImage" className="cursor-pointer">
                  {profileImage ? (
                    <img src={profileImage} alt="Profile" className="w-32 h-32 rounded-full object-cover border-4 border-white" />
                  ) : (
                    <div className="w-32 h-32 rounded-full bg-gray-300 flex items-center justify-center text-gray-500 border-4 border-white">
                      <FaCamera size={24} />
                      <p className="mt-2">Foto</p>
                    </div>
                  )}
                  <input
                    type="file"
                    id="profileImage"
                    accept="image/*"
                    onChange={handleProfileImageChange}
                    className="hidden"
                  />
                </label>
              </div>
            </div>

            {/* Campos de Edição */}
            <div className="space-y-4 mt-6">
              <div className="flex items-center">
                <FaSuitcase className="text-gray-600 mr-2" />
                <input
                  type="text"
                  value={businessType}
                  onChange={(e) => setBusinessType(e.target.value)}
                  className="w-full rounded-md border-gray-300 focus:border-blue-500 focus:ring-blue-500"
                />
              </div>

              <div className="flex items-center">
                <FaHouse className="text-gray-600 mr-2" />
                <input
                  type="text"
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                  className="w-full bg-gray-850 rounded-md border-gray-300 focus:border-blue-500 focus:ring-blue-500"
                />
              </div>

              <div className="flex items-center">
                <FaLocationPin className="text-gray-600 mr-2" />
                <input
                  type="text"
                  value={location}
                  onChange={(e) => setLocation(e.target.value)}
                  className="w-full bg-gray-850 rounded-md border-gray-300 focus:border-blue-500 focus:ring-blue-500"
                />
              </div>

              <div className="flex items-center">
                <p className="text-gray-600 mr-2" />
                <input
                  type="number"
                  value={connections}
                  onChange={(e) => setConnections(e.target.value)}
                  className="w-full rounded-md border-gray-300 focus:border-blue-500 focus:ring-blue-500"
                />
              </div>
            </div>

            <div className="mt-6 flex justify-end space-x-4">
              <button
                onClick={saveChanges}
                className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 focus:outline-none"
              >
                <FaSave className="inline-block mr-1" /> Salvar Alterações
              </button>
              <Link
                to="/"
                className="px-4 py-2 bg-gray-300 text-gray-800 rounded hover:bg-gray-400 focus:outline-none"
              >
                Cancelar
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
